// src/components/UpcomingMatches.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './MatchCard.css';

export default function UpcomingMatches() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('/api/upcoming')
      .then(res => res.json())
      .then(data => setMatches(data.data || []))
      .catch(err => console.error('Failed to load upcoming matches', err))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>Loading upcoming matches...</p>;
  if (matches.length === 0) return <p>No upcoming matches found.</p>;

  return (
    <div className="match-list">
      {matches.map(match => (
        <Link to={`/match/${match.id}`} key={match.id} className="match-card">
          <div className="match-header">
            <img src={match.localteam.image_path} alt={match.localteam.name} />
            <span>{match.localteam.name}</span>
            <strong>vs</strong>
            <span>{match.visitorteam.name}</span>
            <img src={match.visitorteam.image_path} alt={match.visitorteam.name} />
          </div>
          <div className="match-note">
            {new Date(match.starting_at).toLocaleString()}
          </div>
        </Link>
      ))}
    </div>
  );
}
