import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/UpcomingSeries.css';

const UpcomingSeries = () => {
  const [upcomingSeries, setUpcomingSeries] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUpcoming = async () => {
      try {
        const res = await fetch(`/api/series`);
        const data = await res.json();
        const allSeries = data?.data || [];

        const tomorrow = new Date();
        tomorrow.setDate(tomorrow.getDate());

        const filtered = allSeries.filter(s => {
          const start = new Date(s.startDate);
          return start > tomorrow;
        });

        const sorted = filtered.sort(
          (a, b) => new Date(a.startDate) - new Date(b.startDate)
        );

        setUpcomingSeries(sorted.slice(0, 4));
      } catch (err) {
        console.error('Failed to load series:', err);
      }
    };

    fetchUpcoming();
  }, []);

  const getOrdinal = (n) => {
    const s = ["th", "st", "nd", "rd"],
      v = n % 100;
    return n + (s[(v - 20) % 10] || s[v] || s[0]);
  };

  const formatFullDate = (input, fallbackYear = null, startMonth = null) => {
    if (!input) return 'Invalid date';

    let date;

    if (/^\d{4}-\d{2}-\d{2}$/.test(input)) {
      date = new Date(input);
    } else if (/^[A-Za-z]{3,9} \d{1,2}$/.test(input) && fallbackYear !== null) {
      const tempDate = new Date(`${input} ${fallbackYear}`);
      const endMonth = tempDate.getMonth();
      const adjustedYear = (startMonth !== null && endMonth < startMonth) ? fallbackYear + 1 : fallbackYear;
      date = new Date(`${input} ${adjustedYear}`);
    } else {
      date = new Date(input);
    }

    if (isNaN(date)) return 'Invalid date';

    const day = getOrdinal(date.getDate());
    const month = date.toLocaleString('default', { month: 'long' });
    const year = date.getFullYear();

    return `${month} ${day}, ${year}`;
  };

  const formattedDate = (input) => {
    if (!input) return 'N/A';
    try {
      return new Date(input).toLocaleString();
    } catch (e) {
      return 'Invalid date';
    }
  };

  const formatDateRange = (startDate, endDate) => {
    const start = new Date(startDate);
    const end = new Date(endDate);

    const options = { day: 'numeric', month: 'short' };
    const startStr = start.toLocaleDateString('en-GB', options);
    const endStr = end.toLocaleDateString('en-GB', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
    });

    return `${startStr} to ${endStr}`;
  };

  return (
    <div className="upcoming-series-widget">
      <div className="header">
        <h3>ðŸ“… Upcoming Series</h3>
        <a href="/series">
          View All
        </a>
      </div>

      <div className="series-list">
        {upcomingSeries.map((s) => (
          <div className="series-card" key={s.id} onClick={() => navigate(`/series/${s.id}`)}>
            <div className="flags">
              {s.team1_flag && <img src={s.team1_flag} alt="" />}
              {s.team2_flag && <img src={s.team2_flag} alt="" />}
            </div>
            <div className="series-info">
              <p className="series-name">{s.name}</p>
              <p className="series-dates">
                {formatFullDate(s.startDate, s.endDate)}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UpcomingSeries;
