import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match }) => {
  const battingData = match?.match?.batting || [];
  const bowlingData = match?.match?.bowling || [];
  const scoreboards = match?.match?.scoreboards || [];
  const runs = match?.match?.runs || [];
  const lineup = match?.match?.lineup || [];

  const getPlayerName = (id) => {
    const player = lineup.find(p => p.id === id);
    return player ? player.fullname : 'N/A';
  };

  const renderExtrasAndTotal = (scoreboardCode) => {
    const extras = scoreboards.find(sb => sb.type === 'extra' && sb.scoreboard === scoreboardCode);
    const total = scoreboards.find(sb => sb.type === 'total' && sb.scoreboard === scoreboardCode);

    return (
      <>
        {extras && (
          <tr className="extras">
            <td colSpan="2"><strong>Extras</strong></td>
            <td colSpan="3">
              {[
                extras.wide && `Wide: ${extras.wide}`,
                extras.noball_runs && `No Ball: ${extras.noball_runs}`,
                extras.bye && `Bye: ${extras.bye}`,
                extras.leg_bye && `Leg Bye: ${extras.leg_bye}`,
              ].filter(Boolean).join(', ')}
            </td>
          </tr>
        )}
        {total && (
          <tr className="total">
            <td colSpan="2"><strong>Total</strong></td>
            <td colSpan="3"><strong>{total.total} / {total.wickets} in {total.overs} overs</strong></td>
          </tr>
        )}
      </>
    );
  };

  const renderFallOfWickets = (battingForScoreboard) => {
    const fall = battingForScoreboard
      .filter(b => b.fow_score && b.fow_balls)
      .map(b => {
        const name = getPlayerName(b.player_id);
        return `${name} (${b.fow_score} runs, ${b.fow_balls} ov)`;
      });

    return fall.length ? (
      <div className="fow">
        <strong>Fall of Wickets:</strong> {fall.join(', ')}
      </div>
    ) : null;
  };

  const renderInnings = (scoreboardCode, index) => {
    const battingForScoreboard = battingData.filter(b => b.scoreboard === scoreboardCode);
    const bowlingForScoreboard = bowlingData.filter(b => b.scoreboard === scoreboardCode);
    if (!battingForScoreboard.length && !bowlingForScoreboard.length) return null;

    return (
      <div key={scoreboardCode} className="innings-section">
        <h3>{index === 0 ? '1st Innings' : '2nd Innings'}</h3>

        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Batsman</th>
              <th>R</th>
              <th>B</th>
              <th>4s</th>
              <th>6s</th>
            </tr>
          </thead>
          <tbody>
            {battingForScoreboard.map((b, i) => (
              <tr key={i}>
                <td>{getPlayerName(b.player_id)}</td>
                <td>{b.score}</td>
                <td>{b.ball}</td>
                <td>{b.four_x}</td>
                <td>{b.six_x}</td>
              </tr>
            ))}
            {renderExtrasAndTotal(scoreboardCode)}
          </tbody>
        </table>

        {renderFallOfWickets(battingForScoreboard)}

        <h4>Bowling</h4>
        <table className="scorecard-table bowling">
          <thead>
            <tr>
              <th>Bowler</th>
              <th>O</th>
              <th>R</th>
              <th>W</th>
              <th>Eco</th>
            </tr>
          </thead>
          <tbody>
            {bowlingForScoreboard.map((b, i) => (
              <tr key={i}>
                <td>{getPlayerName(b.player_id)}</td>
                <td>{b.overs}</td>
                <td>{b.runs}</td>
                <td>{b.wickets}</td>
                <td>{b.rate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  const scoreboardsUsed = [...new Set(battingData.map(b => b.scoreboard))];

  return (
    <div className="scorecard-tab">
      {scoreboardsUsed.map((sb, i) => renderInnings(sb, i))}
    </div>
  );
};

export default ScorecardTab;
