import React, { useEffect, useState } from 'react';
import './SeriesSquad.css';

const SeriesSquad = () => {
  const seriesId = window.location.pathname.split('/')[2]; // get series ID from URL
  const [squads, setSquads] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!seriesId) return;

    const fetchSquads = async () => {
      try {
        const res = await fetch(`/api/series/${seriesId}/squads`);
console.log(res);
        const json = await res.json();
        const grouped = groupByTeam(json.data || []);
        setSquads(grouped);
      } catch (err) {
        console.error('Error loading series squads', err);
      } finally {
        setLoading(false);
      }
    };

    fetchSquads();
  }, [seriesId]);
console.log(squads);
  const groupByTeam = (players) => {
    const map = {};
    for (const p of players) {
      if (!map[p.players]) map[p.players] = [];
      map[p.players].push(p);
    }
    return Object.entries(map).map(([team, players]) => ({ team, players }));
  };

  return (
    <div className="series-squad">
      <h3>ðŸ§¢ Squads</h3>
      {loading ? (
        <p>Loading squads...</p>
      ) : squads.length === 0 ? (
        <p>No squad data available.</p>
      ) : (
        squads.map(({ team, players }) => (
          <div key={team} className="squad-team">
            <h4>{team}</h4>
            <ul>
              {players.map((player) => (
                <li key={player.id}>
                  {player.name}
                  {player.role ? ` â€“ ${player.role}` : ''}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default SeriesSquad;
