import React, { useState, useEffect } from 'react';
import './css/Rankings.css';

const Rankings = () => {
  const [rankings, setRankings] = useState([]);

  useEffect(() => {
    const fetchRankings = async () => {
      try {
        const res = await fetch(`/api/team-rankings`);
        const data = await res.json();
        setRankings(data?.data || []);
      } catch (err) {
        console.error('Error fetching rankings:', err);
      }
    };
    fetchRankings();
  }, []);

  const getFormatData = (type) =>
    rankings.find(r => r.type === type)?.team
      ?.filter(t => t.ranking)
      ?.sort((a, b) => a.ranking.position - b.ranking.position)
      ?.slice(0, 5) || [];

  const renderFormatTable = (formatLabel, type) => {
    const teams = getFormatData(type);
    return (
      <div className="ranking-column">
        <h3>{formatLabel}</h3>
        {teams.map((team, index) => (
          <div key={team.id} className="ranking-row">
            <span className={`pos rank-${index + 1}`}>{index + 1}</span>
            <img
              src={team.image_path}
              alt={team.name}
              className="flag"
              onError={(e) => {
                e.target.src = 'https://em-content.zobj.net/source/apple/354/white-flag_1f3f3-fe0f.png';
              }}
            />
            <span className="team-name">{team.name}</span>
            <span className="rating">{team.ranking.rating}</span>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="icc-ranking-section">
      <div className="ranking-header">
        <h3>üèè ICC Team Ranking</h3>
        <a href="/rankings">View All</a>
      </div>
      <div className="ranking-columns">
        {renderFormatTable('Test', 'TEST')}
        {renderFormatTable('ODI', 'ODI')}
        {renderFormatTable('T20', 'T20I')}
      </div>
    </div>
  );
};

export default Rankings;
