// src/components/LiveMatches.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './MatchCard.css';

export default function LiveMatches() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('/api/live')
      .then(res => res.json())
      .then(data => setMatches(data.data || []))
      .catch(err => console.error('Failed to load live matches', err))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>Loading live matches...</p>;
  if (matches.length === 0) return <p>No live matches at the moment.</p>;

  return (
    <div className="match-list">
      {matches.map(match => (
        <Link to={`/match/${match.id}`} key={match.id} className="match-card">
          <div className="match-header">
            <img src={match.localteam.image_path} alt={match.localteam.name} />
            <span>{match.localteam.name}</span>
            <strong>vs</strong>
            <span>{match.visitorteam.name}</span>
            <img src={match.visitorteam.image_path} alt={match.visitorteam.name} />
          </div>
          {match.scoreboards?.length > 0 && (
            <div className="scores">
              {match.scoreboards
                .filter(sb => sb.type === 'total')
                .map(sb => {
                  const team = match.localteam.id === sb.team_id
                    ? match.localteam
                    : match.visitorteam;
                  return (
                    <p key={sb.id}>
                      <strong>{team.name}</strong>: {sb.total}/{sb.wickets} ({sb.overs} ov)
                    </p>
                  );
                })}
            </div>
          )}
          {match.note && <div className="match-note">{match.note}</div>}
        </Link>
      ))}
    </div>
  );
}
