import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import './TeamSeries.css';

const TeamSeries = () => {
  const { id } = useParams();
  const { teamName } = useParams();
  const [seriesList, setSeriesList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('upcoming');
  const now = new Date();

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const res = await fetch(`/api/series?search=${encodeURIComponent(teamName)}`);
        const json = await res.json();
        setSeriesList(json.data || []);
      } catch (err) {
        console.error('Error fetching series:', err);
        setSeriesList([]);
      } finally {
        setLoading(false);
      }
    };

    fetchSeries();
  }, [teamName]);

  const getOrdinal = (n) => {
    const s = ["th", "st", "nd", "rd"],
      v = n % 100;
    return n + (s[(v - 20) % 10] || s[v] || s[0]);
  };

const parseDate = (raw, fallbackYear = null, startMonth = null) => {
  if (!raw) return null;

  // Full date
  if (/^\d{4}-\d{2}-\d{2}$/.test(raw)) return new Date(raw);

  // "Jul 27" format + fallback year
  if (/^[A-Za-z]{3,9} \d{1,2}$/.test(raw) && fallbackYear !== null) {
    const tempDate = new Date(`${raw} ${fallbackYear}`);
    const endMonth = tempDate.getMonth();
    const adjustedYear = (startMonth !== null && endMonth < startMonth) ? fallbackYear + 1 : fallbackYear;
    return new Date(`${raw} ${adjustedYear}`);
  }

  return new Date(raw);
};

const upcomingSeries = seriesList.filter(s => {
  const start = parseDate(s.startDate);
  return start && start > now;
});

const liveSeries = seriesList.filter(s => {
  const start = parseDate(s.startDate);
  const end = parseDate(s.endDate, start?.getFullYear(), start?.getMonth());
  return start && end && start <= now && end >= now;
});

const completedSeries = seriesList.filter(s => {
  const start = parseDate(s.startDate);
  const end = parseDate(s.endDate, start?.getFullYear(), start?.getMonth());
  return end && end < now;
});


  const formatFullDate = (input, fallbackYear = null, startMonth = null) => {
    if (!input) return 'Invalid date';

    let date;

    if (/^\d{4}-\d{2}-\d{2}$/.test(input)) {
      date = new Date(input);
    } else if (/^[A-Za-z]{3,9} \d{1,2}$/.test(input) && fallbackYear !== null) {
      const tempDate = new Date(`${input} ${fallbackYear}`);
      const endMonth = tempDate.getMonth();
      const adjustedYear = (startMonth !== null && endMonth < startMonth) ? fallbackYear + 1 : fallbackYear;
      date = new Date(`${input} ${adjustedYear}`);
    } else {
      date = new Date(input);
    }

    if (isNaN(date)) return 'Invalid date';

    const day = getOrdinal(date.getDate());
    const month = date.toLocaleString('default', { month: 'long' });
    const year = date.getFullYear();

    return `${month} ${day}, ${year}`;
  };

return (
  <div className="team-series-page">
    <h2>ðŸ“… {teamName} Cricket Team</h2>
    {loading ? (
      <p>Loading series...</p>
    ) : seriesList.length === 0 ? (
      <p>No series or matches found for {teamName} team.</p>
    ) : (
      <>
        <div className="tabs">
          <button
            className={activeTab === 'upcoming' ? 'active' : ''}
            onClick={() => setActiveTab('upcoming')}
          >
            Upcoming
          </button>
          <button
            className={activeTab === 'live' ? 'active' : ''}
            onClick={() => setActiveTab('live')}
          >
            Live
          </button>
          <button
            className={activeTab === 'completed' ? 'active' : ''}
            onClick={() => setActiveTab('completed')}
          >
            Completed
          </button>
        </div>

        <div className="series-grid">
          {(activeTab === 'upcoming' && upcomingSeries.length === 0) ||
          (activeTab === 'live' && liveSeries.length === 0) ||
          (activeTab === 'completed' && completedSeries.length === 0) ? (
            <p style={{ marginTop: '20px' }}>
              No {activeTab} series found for {teamName} Cricket Team.
            </p>
          ) : (
            (activeTab === 'upcoming' ? upcomingSeries :
              activeTab === 'live' ? liveSeries :
              completedSeries
            ).map((s) => (
              <div
                key={s.id}
                className="series-card"
                onClick={() => window.location.href = `/series/${s.id}`}
              >
                <h3>{s.name}</h3>
                <p>Start Date - {formatFullDate(s.startDate)}</p>
                <p>End Date - {formatFullDate(
                  s.endDate,
                  new Date(s.startDate).getFullYear(),
                  new Date(s.startDate).getMonth()
                )}</p>
                <p>TEST - {s.test}, ODI - {s.odi}, T20 - {s.t20}</p>
                <p>Total Matches - {(s.test || 0) + (s.odi || 0) + (s.t20 || 0)}</p>
              </div>
            ))
          )}
        </div>
      </>
    )}
  </div>
);
};

export default TeamSeries;
