import React from 'react';

const CommentaryTab = ({ commentaries }) => {
  if (!commentaries || commentaries.length === 0) {
    return <div>No commentary available for this match.</div>;
  }

  // Sort commentaries by descending order of over/ball
  const sorted = [...commentaries].sort((a, b) => {
    const aOver = parseFloat(a.over);
    const bOver = parseFloat(b.over);
    return bOver - aOver;
  });

  return (
    <div>
      <h3>Commentary</h3>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {sorted.map((item, index) => (
          <li key={index} style={{ marginBottom: '10px', borderBottom: '1px solid #ccc', paddingBottom: '6px' }}>
            <strong>{item.over} ov:</strong> {item.comment}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CommentaryTab;
