import React, { useEffect, useState } from 'react';
import './TeamRankings.css';

const TeamRankings = () => {
  const [rankings, setRankings] = useState([]);
  const [format, setFormat] = useState('TEST');

  useEffect(() => {
    const fetchRankings = async () => {
      try {
        const response = await fetch('http://localhost:5050/api/rankings');
        const data = await response.json();
        console.log('API response:', data);
        if (Array.isArray(data.data)) {
          setRankings(data.data);
        } else {
          console.error('Rankings data not in expected format:', data);
        }
      } catch (error) {
        console.error('Error fetching rankings:', error);
      }
    };

    fetchRankings();
  }, []);
console.log('Rankings:', rankings);

  // Filter the current ranking object for the selected format
  const currentRanking = rankings.find(r => r.type === format);
  const teams = currentRanking?.team || [];

  return (
    <div className="team-rankings">
      <h2>ICC Team Rankings</h2>
      <div className="ranking-tabs">
        <button onClick={() => setFormat('TEST')} className={format === 'TEST' ? 'active' : ''}>Test</button>
        <button onClick={() => setFormat('ODI')} className={format === 'ODI' ? 'active' : ''}>ODI</button>
        <button onClick={() => setFormat('T20I')} className={format === 'T20I' ? 'active' : ''}>T20</button>
      </div>

      {teams.length === 0 ? (
        <p className="no-data">No ranking data available for {format}</p>
      ) : (
        <table className="ranking-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Team</th>
              <th>Matches</th>
              <th>Points</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
            {teams.map(team => (
              <tr key={team.id}>
                <td>{team.ranking?.position || '-'}</td>
                <td>
                  <img src={team.image_path} alt={team.code} className="team-flag" />
                  {team.name}
                </td>
                <td>{team.ranking?.matches || '-'}</td>
                <td>{team.ranking?.points || '-'}</td>
                <td>{team.ranking?.rating || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default TeamRankings;
