import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch('/api/live');
        const json = await res.json();

        const liveMatches = (json?.data || []).filter(
          m => m.matchStarted && !m.matchEnded && m.status !== 'Match not started'
        );

        setMatches(liveMatches.slice(0, 4));
      } catch (err) {
        console.error('Error fetching live matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  if (loading) return <p>Loading live matches...</p>;
  if (!matches.length) return <p>No live matches found.</p>;

  return (
    <div className="home-container">
      <h2>Live Matches</h2>
      <div className="match-cards-row">
        {matches.map(match => (
          <div
            key={match.id}
            className="match-card"
            onClick={() => navigate(`/match/${match.id}`)}
          >
            <div className="teams">
              {match.teamInfo?.map(team => (
                <div key={team.name} className="team">
                  <img src={team.img} alt={team.name} />
                  <span>{team.shortname}</span>
                </div>
              ))}
            </div>
            {match.score?.length > 0 && (
              <div className="score">
                <p>
                  {match.score[0].inning}: {match.score[0].r}/{match.score[0].w} ({match.score[0].o} ov)
                </p>
              </div>
            )}
            <p className="status">{match.status}</p>
            {match.series_id && (
              <Link
                to={`/series/${match.series_id}`}
                className="series-link"
                state={{ fromHome: true }}
                onClick={(e) => e.stopPropagation()}
              >
                {match.seriesName || 'Series'}
              </Link>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
