// src/pages/Stadiums.js
import React, { useEffect, useState } from 'react';
import './Stadiums.css';

const Stadiums = () => {
  const [venues, setVenues] = useState([]);
  const [countries, setCountries] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [minCap, setMinCap] = useState(0);
  const [maxCap, setMaxCap] = useState(100000);
  const [floodlightOnly, setFloodlightOnly] = useState(false);
  const [sortBy, setSortBy] = useState('name');
  const [sortDir, setSortDir] = useState('asc');
  const [page, setPage] = useState(1);
  const [modalVenue, setModalVenue] = useState(null);

  const PER_PAGE = 16;

  useEffect(() => {
    fetch('/api/venues')
      .then(res => res.json())
      .then(json => setVenues(json.data || []));
    fetch('/api/countries')
      .then(res => res.json())
      .then(json => setCountries(json.data || []));
  }, []);

  useEffect(() => {
    let result = [...venues];

    if (selectedCountry) {
      const countryId = parseInt(selectedCountry);
      result = result.filter(v => v.country_id === countryId);
    }

    if (floodlightOnly) {
      result = result.filter(v => v.floodlight === true);
    }

    result = result.filter(v => {
      const cap = v.capacity || 0;
      return cap >= minCap && cap <= maxCap;
    });

    result.sort((a, b) => {
      const valA = sortBy === 'capacity' ? (a.capacity || 0) : a.name.toLowerCase();
      const valB = sortBy === 'capacity' ? (b.capacity || 0) : b.name.toLowerCase();
      if (valA < valB) return sortDir === 'asc' ? -1 : 1;
      if (valA > valB) return sortDir === 'asc' ? 1 : -1;
      return 0;
    });

    setFiltered(result);
    setPage(1); // reset to first page on filter
  }, [venues, selectedCountry, floodlightOnly, minCap, maxCap, sortBy, sortDir]);

  const paginated = filtered.slice((page - 1) * PER_PAGE, page * PER_PAGE);
  const totalPages = Math.ceil(filtered.length / PER_PAGE);

  return (
    <div className="stadium-container">
      <h2>Stadiums</h2>

      <div className="filters">
        <select value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
          <option value="">All Countries</option>
          {countries.map(c => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>

        <label>
          Capacity Range: {minCap} - {maxCap}
          <input type="range" min="0" max="100000" step="1000" value={minCap}
            onChange={(e) => setMinCap(Number(e.target.value))} />
          <input type="range" min="0" max="100000" step="1000" value={maxCap}
            onChange={(e) => setMaxCap(Number(e.target.value))} />
        </label>

        <label>
          <input type="checkbox" checked={floodlightOnly} onChange={(e) => setFloodlightOnly(e.target.checked)} />
          Floodlight Only
        </label>

        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="name">Sort by Name</option>
          <option value="capacity">Sort by Capacity</option>
        </select>
        <select value={sortDir} onChange={(e) => setSortDir(e.target.value)}>
          <option value="asc">Asc</option>
          <option value="desc">Desc</option>
        </select>
      </div>

      <div className="stadium-grid">
        {paginated.map(stadium => (
          <div key={stadium.id} className="stadium-card" onClick={() => setModalVenue(stadium)}>
            <img src={stadium.image_path} alt={stadium.name} />
            <h4>{stadium.name}</h4>
            <p>{stadium.city}</p>
            <p>Capacity: {stadium.capacity || '--'}</p>
          </div>
        ))}
      </div>

      <div className="pagination">
        <button disabled={page <= 1} onClick={() => setPage(p => p - 1)}>← Previous</button>
        <span>Page {page} of {totalPages}</span>
        <button disabled={page >= totalPages} onClick={() => setPage(p => p + 1)}>Next →</button>
      </div>

      {modalVenue && (
        <div className="modal" onClick={() => setModalVenue(null)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <h3>{modalVenue.name}</h3>
            <img src={modalVenue.image_path} alt={modalVenue.name} />
            <p><strong>City:</strong> {modalVenue.city}</p>
            <p><strong>Capacity:</strong> {modalVenue.capacity || '--'}</p>
            <p><strong>Floodlight:</strong> {modalVenue.floodlight ? 'Yes' : 'No'}</p>
            <button onClick={() => setModalVenue(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Stadiums;
