import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/TeamFixtures.css';

const TeamFixtures = () => {
  const [teams, setTeams] = useState([]);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [fallbackImg, setFallbackImg] = useState(null);

  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const res = await fetch(`/api/teams`);
        const json = await res.json();
        setTeams(json.data || []);
      } catch (err) {
        console.error('Error loading teams:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeams();
  }, []);

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('/api/default-images');
        const text = await res.text();
        const match = text.match(/flag_url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  return (
    <div className="home-teams-page">
      <h2>üèÅ Cricket Teams</h2>

      <div className="home-teams-grid">
        {teams.slice(0, 7).map((team) => (
          <div
            key={team.id}
            className="home-team-card"
            onClick={() => navigate(`/teams/${team.name}/series`)}
          >
            <img
              src={
                team.image_path && team.image_path.includes('h.cricapi.com')
                  ? fallbackImg || '' // wait for fallbackImg
                  : team.image_path
              }
              alt={team.name}
            />
              <p>{team.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeamFixtures;
