import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5050/api/live')
      .then(res => {
        setMatches(res.data.data || []);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching live matches:', err);
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ padding: 20, fontFamily: 'sans-serif' }}>
      <h1>üèè Live Cricket Scores</h1>

      {loading && <p>Loading live matches...</p>}

      {!loading && matches.length === 0 && <p>No live or recent matches available.</p>}

      {matches.map(match => {
        const local = match.localteam;
        const visitor = match.visitorteam;
        const scoreboardLocal = match.scoreboards.find(s => s.team_id === local.id && s.type === 'total');
        const scoreboardVisitor = match.scoreboards.find(s => s.team_id === visitor.id && s.type === 'total');

        return (
          <div key={match.id} style={{ border: '1px solid #ddd', borderRadius: 10, margin: '20px 0', padding: 15 }}>
            <h3>{local.name} vs {visitor.name}</h3>
            <p>Status: <strong>{match.status}</strong></p>
            <p>
              <img src={local.image_path} alt={local.code} width={32} />{' '}
              {scoreboardLocal ? `${scoreboardLocal.total}/${scoreboardLocal.wickets} in ${scoreboardLocal.overs} overs` : 'N/A'}
              <br />
              <img src={visitor.image_path} alt={visitor.code} width={32} />{' '}
              {scoreboardVisitor ? `${scoreboardVisitor.total}/${scoreboardVisitor.wickets} in ${scoreboardVisitor.overs} overs` : 'N/A'}
            </p>
            {match.note && <p><em>{match.note}</em></p>}
          </div>
        );
      })}
    </div>
  );
}

export default App;
