import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './css/Series.css'; // Make sure to create this CSS file

export default function Series() {
  const [seriesList, setSeriesList] = useState([]);
  const [search, setSearch] = useState('');
  const [fallbackImg, setFallbackImg] = useState(null);

  useEffect(() => {
    axios.get('/api/series').then(res => {
      setSeriesList(res.data.data || []);
    });
  }, []);

  const filtered = seriesList.filter(s =>
    s.name?.toLowerCase().includes(search.toLowerCase())
  );

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('/api/default-images');
        const text = await res.text();
        const match = text.match(/series_url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  const seriesYear = (series) => { 
    series.season?.code || series.season?.name;
  };

  return (
    <div className="series-container">
      <h1>All Series</h1>
      <input
        type="text"
        placeholder="Search series..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="series-search"
      />

      <div className="series-grid">
        {filtered.map(series => (
          <Link
            key={series.id}
            to={`/series/${series.id}/${series.season_id}`}
            className="series-card"
          >
            <img
              src={
                series.image_path && !series.image_path.endsWith('sportmonks.com')
                  ? series.image_path
                  : `fallbackImg`
              }
              alt={series.name}
              onError={(e) => { e.target.src = `fallbackImg`; }}
            />
            <div className="series-info">
              <h4>{series.name}</h4>
              <p>{seriesYear}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
