// src/components/Players.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './Players.css';

export default function Players() {
  // paged list
  const [players, setPlayers] = useState([]);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [loadingList, setLoadingList] = useState(false);

  // search
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const searchTimeout = useRef(null);

  // reference data
  const [countries, setCountries] = useState({});
  const [positions, setPositions] = useState({});

  // modal
  const [modalPlayer, setModalPlayer] = useState(null);
  const [modalDetails, setModalDetails] = useState(null);
  const [loadingModal, setLoadingModal] = useState(false);

  // fetch reference lists once
  useEffect(() => {
    axios.get('/api/countries')
      .then(r => {
        const map = {};
        r.data.data.forEach(c => map[c.id] = c);
        setCountries(map);
      })
      .catch(console.error);

    axios.get('/api/positions')
      .then(r => {
        const map = {};
        r.data.data.forEach(p => map[p.id] = p.name);
        setPositions(map);
      })
      .catch(console.error);
  }, []);

  // load one page of players
  const loadPlayersPage = async (pg = 1) => {
    setLoadingList(true);
    try {
      const res = await axios.get(`/api/players-short?page=${pg}&perPage=10`);
      const batch = res.data.data || [];
      if (batch.length < 10) setHasMore(false);
      setPlayers(prev => pg === 1 ? batch : [...prev, ...batch]);
      setPage(pg);
    } catch (err) {
      console.error('❌ Error loading players page', err);
    } finally {
      setLoadingList(false);
    }
  };

  // initial load
  useEffect(() => {
    loadPlayersPage(1);
  }, []);

  // search handler (debounced)
  const handleSearchChange = (e) => {
    const val = e.target.value;
    setSearchTerm(val);

    clearTimeout(searchTimeout.current);
    if (!val.trim()) {
      setSearchResults([]);
      return;
    }

    searchTimeout.current = setTimeout(async () => {
      try {
        const res = await axios.get('/api/players-search', {
          params: { q: val, page: 1, perPage: 10 }
        });
        setSearchResults(res.data.data || []);
      } catch (err) {
        console.error('❌ Search failed', err);
      }
    }, 300);
  };

  // load modal details
  useEffect(() => {
    if (!modalPlayer) return;
    setLoadingModal(true);
    axios.get(`/api/player/${modalPlayer.id}`)
      .then(r => setModalDetails(r.data))
      .catch(console.error)
      .finally(() => setLoadingModal(false));
  }, [modalPlayer]);

  // close modal
  const closeModal = () => {
    setModalPlayer(null);
    setModalDetails(null);
  };

  // choose which list to render
  const displayList = searchTerm ? searchResults : players;

  return (
    <div className="players-container">
      <h2>Players</h2>

      {/* Search Bar */}
      <input
        type="text"
        className="search-bar"
        placeholder="Search players..."
        value={searchTerm}
        onChange={handleSearchChange}
      />

      {/* Search Suggestions */}
      {searchTerm && (
        <ul className="suggestions-list">
          {searchResults.length > 0 ? (
            searchResults.map(p => (
              <li key={p.id} onClick={() => setModalPlayer(p)}>
                {p.fullname}
              </li>
            ))
          ) : (
            <li>No matches</li>
          )}
        </ul>
      )}

      {/* Player Grid */}
      <div className="players-grid">
        {displayList.map(p => {
          const country = countries[p.country_id];
          return (
            <div
              key={p.id}
              className="player-card"
              onClick={() => setModalPlayer(p)}
            >
              <img
                src={p.image_path}
                alt={p.fullname}
                className="player-img"
              />
              <h4>{p.fullname}</h4>
              {country && (
                <div className="flag-info">
                  <img
                    src={country.image_path}
                    alt={country.name}
                    className="flag-icon"
                  />
                  <span>{country.name}</span>
                </div>
              )}
            </div>
          );
        })}
      </div>

      {/* Load More */}
      {!searchTerm && hasMore && (
        <button
          className="load-more-btn"
          onClick={() => loadPlayersPage(page + 1)}
          disabled={loadingList}
        >
          {loadingList ? 'Loading…' : 'Load More'}
        </button>
      )}

      {/* Modal */}
      {modalPlayer && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            {loadingModal || !modalDetails ? (
              <p>Loading details…</p>
            ) : (
              <>
                <button className="close-btn" onClick={closeModal}>✖</button>
                <h3>{modalDetails.fullname}</h3>
                <img
                  src={modalDetails.image_path}
                  alt={modalDetails.fullname}
                  className="player-modal-img"
                />
                <p><strong>Country:</strong> {modalDetails.country?.name}</p>
                <p><strong>Position:</strong> {modalDetails.position?.name}</p>
                <p><strong>DOB:</strong> {modalDetails.dateofbirth}</p>
                <p><strong>Batting:</strong> {modalDetails.battingstyle}</p>
                <p><strong>Bowling:</strong> {modalDetails.bowlingstyle}</p>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
