import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './css/PlayerDetails.css';

const STAT_ORDER = ['m', 'inn', 'runs', 'bf', 'hs', 'avg', 'sr', 'no', '4s', '6s', '50', '100', '200'];
const BOWLING_STAT_ORDER = ['m', 'inn', 'b', 'runs', 'wkts', 'avg', 'econ', 'sr', 'bbi', 'bbm', '5w', '10w'];

export default function PlayerDetail() {
  const { id } = useParams(); // CricketData player ID
  const [player, setPlayer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [articles, setArticles] = useState([]);
  const [sportmonks, setSportmonks] = useState([]);
  const [image, setImage] = useState(null);

  const fetchSportmonksPlayers = async () => {
    const res = await fetch('/api/sportmonks/players');
    const data = await res.json();
    setSportmonks(data?.data || []);
  };

  const load = async () => {
    try {
      const res = await fetch(`/api/player/${id}`);
      const data = await res.json();
      setPlayer(data.data);

      // 👇 After setting player, fetch news
      const playerName = data.data?.name?.replace(/\s+/g, '+');
      if (playerName) {
        try {
          const response = await fetch(`/api/news?q=${playerName}`);
          const news = await response.json();
          setArticles(news.articles || []);
        } catch (err) {
          console.error('Error fetching news articles', err);
        } finally {
          setLoading(false);
        }
      } else {
        setLoading(false);
      }
    } catch (err) {
      console.error('Failed to fetch player data:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, [id]);


useEffect(() => {
  if (player?.name && sportmonks.length > 0) {
    setImage(getImage(player.name));
  }
}, [player, sportmonks]);

  if (loading) return <p>Loading player details...</p>;
  if (!player) return <p>Player not found.</p>;

  const { name, country, dateOfBirth, role, battingStyle, bowlingStyle, placeOfBirth, playerImg, stats = [] } = player;

  const getStats = (type, matchType) => {
    const filtered = stats
      .filter((s) => s.fn === type && s.matchtype === matchType)
      .reduce((acc, s) => {
        acc[s.stat.trim()] = s.value.trim();
        return acc;
      }, {});
    return filtered;
  };

  const renderStatTable = (matchTypes, type, order) => {
    return (
      <table>
        <thead>
          <tr>
            <th> </th>
            {order.map((stat) => (
              <th key={stat}>{stat.toUpperCase()}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {matchTypes.map((matchType) => {
            const row = getStats(type, matchType);
            return (
              <tr key={matchType}>
                <td>{matchType.toUpperCase()}</td>
                {order.map((s) => (
                  <td key={s}>{row[s] || '-'}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  };

  return (
    <div className="player-detail">
      <Link to="/player" className="back-link">← Back to Players</Link>
      <h2>{name}</h2>
      <p><strong>Country:</strong> {country}</p>
      <p><strong>Date of Birth:</strong> {dateOfBirth?.slice(0, 10)}</p>
      <p><strong>Role:</strong> {role}</p>
      <p><strong>Batting Style:</strong> {battingStyle}</p>
      <p><strong>Bowling Style:</strong> {bowlingStyle}</p>
      <p><strong>Place of Birth:</strong> {placeOfBirth}</p>

 <img className="player-image" src={image} alt={player.name} />
      <div className="player-stats">
        <h3>Batting Career Summary</h3>
        {renderStatTable(['test', 'odi', 't20', 'ipl'], 'batting', STAT_ORDER)}

        <h3>Bowling Career Summary</h3>
        {renderStatTable(['test', 'odi', 't20', 'ipl'], 'bowling', BOWLING_STAT_ORDER)}
      </div>

      {loading ? (
        <p>Loading articles...</p>
      ) : articles.length === 0 ? (
        <></>
      ) : (
        <>
          <h3>News</h3>
          <div className="news-container">
            <div className="news-grid">
              {articles.map((article, index) => (
                <div key={index} className="news-card">
                  <img
                    src={article.image}
                    alt={article.title}
                    className="news-image"
                  />
                  <h2 className="news-heading">{article.title}</h2>
                  <p className="news-meta">
                    {article.source.name} •{' '}
                    {new Date(article.publishedAt).toLocaleString()}
                  </p>
                  <p className="news-description">{article.description}</p>
                  <a
                    href={article.url}
                    target="_blank"
                    rel="noreferrer"
                    className="news-link"
                  >
                    Read more →
                  </a>
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );
}
