// src/context/SeriesMatchesContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';

const SeriesMatchesContext = createContext();

export const SeriesMatchesProvider = ({ seriesId, children }) => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!seriesId) return;

    const fetchSeriesMatches = async () => {
      try {
        const res = await axios.get(`/api/series/${seriesId}/matches`);
        setMatches(res.data?.data?.data || []);
      } catch (err) {
        console.error('❌ Error fetching series matches:', err);
        setMatches([]);
      } finally {
        setLoading(false);
      }
    };

    fetchSeriesMatches();
  }, [seriesId]);

  return (
    <SeriesMatchesContext.Provider value={{ matches, loading }}>
      {children}
    </SeriesMatchesContext.Provider>
  );
};

export const useSeriesMatches = () => useContext(SeriesMatchesContext);
