import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match }) => {
  if (!match || !match.scoreboards) return <div>No scorecard data available.</div>;

  // Group scoreboards by innings (S1, S2, etc.)
  const inningsGroups = match.scoreboards.reduce((groups, sb) => {
    const key = sb.scoreboard;
    if (!groups[key]) groups[key] = [];
    groups[key].push(sb);
    return groups;
  }, {});

  const renderInnings = (scoreboardKey, index) => {
    const scoreboards = inningsGroups[scoreboardKey];
    const totalData = scoreboards.find(sb => sb.type === 'total');
    const extras = scoreboards.find(sb => sb.type === 'extra');

    if (!totalData) return null;

    const batting = match.batting?.filter(b => b.scoreboard === scoreboardKey) || [];
    const bowling = match.bowling?.filter(b => b.scoreboard === scoreboardKey) || [];

    const battingTeam = match.localteam?.id === totalData.team_id
      ? match.localteam
      : match.visitorteam;

    const totalExtras =
      (extras?.wide || 0) +
      (extras?.noball_runs || 0) +
      (extras?.bye || 0) +
      (extras?.leg_bye || 0) +
      (extras?.penalty || 0);

    return (
      <div className="innings-block" key={scoreboardKey}>
        <h3>{index === 0 ? '1st Innings' : '2nd Innings'} - {battingTeam?.name}</h3>

        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Batsman</th>
              <th>Dismissal</th>
              <th>R</th>
              <th>B</th>
              <th>4s</th>
              <th>6s</th>
              <th>SR</th>
            </tr>
          </thead>
          <tbody>
            {batting.map(player => (
              <tr key={player.id}>
                <td>{player.batsman?.fullname || 'N/A'}</td>
                <td>{player.result?.name || 'Not out'}</td>
                <td>{player.score}</td>
                <td>{player.ball}</td>
                <td>{player.four}</td>
                <td>{player.six}</td>
                <td>{player.rate}</td>
              </tr>
            ))}
            <tr>
              <td colSpan="3"><strong>Extras</strong></td>
              <td colSpan="4">
                {totalExtras} (
                {extras?.wide || 0}w, {extras?.noball_runs || 0}nb, {extras?.bye || 0}b, {extras?.leg_bye || 0}lb)
              </td>
            </tr>
            <tr>
              <td colSpan="3"><strong>Total</strong></td>
              <td colSpan="4">
                <strong>{totalData.total}/{totalData.wickets}</strong> ({totalData.overs} overs)
              </td>
            </tr>
          </tbody>
        </table>

        <h4>Fall of Wickets</h4>
        {batting
          .filter(b => b.score && b.ball && b.out)
          .map((wicket, idx) => (
            <p key={wicket.id}>
              {idx + 1}. {wicket.batsman?.fullname || 'Batsman'} - {wicket.score} ({wicket.ball} balls)
            </p>
          ))}

        <h4>Bowling</h4>
        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Bowler</th>
              <th>O</th>
              <th>M</th>
              <th>R</th>
              <th>W</th>
              <th>Eco</th>
            </tr>
          </thead>
          <tbody>
            {bowling.map(player => (
              <tr key={player.id}>
                <td>{player.bowler?.fullname || 'N/A'}</td>
                <td>{player.overs}</td>
                <td>{player.medians}</td>
                <td>{player.runs}</td>
                <td>{player.wickets}</td>
                <td>{player.rate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="scorecard-container">
      {Object.keys(inningsGroups).map((key, index) =>
        renderInnings(key, index)
      )}
    </div>
  );
};

export default ScorecardTab;
