import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from './CountdownTimer';
import './css/TeamMatches.css';

export default function TeamMatches() {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [teamNames, setTeamNames] = useState({});
  const [venueData, setVenueData] = useState({});
  const [activeTab, setActiveTab] = useState(null);
  const navigate = useNavigate();

  const liveStatuses = ['LIVE', '1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4', 'Tea Break', 'Lunch', 'Dinner', 'Innings Break', 'Int.', 'Delayed'];
  const upcomingStatuses = ['NS', 'Postp.'];
  const completedStatuses = ['Finished', 'Cancl', 'Aban.'];

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        const fixtures = res.data.data.fixtures || [];
        setMatches(fixtures);

        const teamIds = new Set();
        const venueIds = new Set();

        fixtures.forEach((match) => {
          teamIds.add(match.localteam_id);
          teamIds.add(match.visitorteam_id);
          venueIds.add(match.venue_id);
        });

        const teamMap = {};
        await Promise.all([...teamIds].map(async (teamId) => {
          try {
            const response = await axios.get(`/api/teams/${teamId}`);
            teamMap[teamId] = response.data.data.data.name;
          } catch {
            teamMap[teamId] = 'Unknown';
          }
        }));
        setTeamNames(teamMap);

        const venueMap = {};
        await Promise.all([...venueIds].map(async (venueId) => {
          try {
            const response = await axios.get(`/api/venues/${venueId}`);
            venueMap[venueId] = response.data.data;
          } catch {
            venueMap[venueId] = {};
          }
        }));
        setVenueData(venueMap);
      } catch (err) {
        console.error('Failed to load matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [id]);

  const getTeamName = (teamId) => teamNames[teamId] || 'TBD';
  const getVenueName = (venueId) => venueData[venueId]?.name || '';
  const getVenueCity = (venueId) => venueData[venueId]?.city || '';

  const liveMatches = matches.filter((m) => liveStatuses.includes(m.status));
  const upcomingMatches = matches.filter((m) => upcomingStatuses.includes(m.status));
  const completedMatches = matches.filter((m) => completedStatuses.includes(m.status));

  const tabData = [
    { key: 'live', label: 'Live', matches: liveMatches },
    { key: 'upcoming', label: 'Upcoming', matches: upcomingMatches },
    { key: 'completed', label: 'Completed', matches: completedMatches },
  ];

  const availableTabs = tabData.filter((tab) => tab.matches.length > 0);
  const finalTabs = availableTabs.length ? availableTabs : [tabData[2]];

  useEffect(() => {
    if (!activeTab && finalTabs.length) {
      setActiveTab(finalTabs[0].key);
    }
  }, [finalTabs, activeTab]);

  const tabMatches = finalTabs.find((t) => t.key === activeTab)?.matches || [];

  return (
    <div className="team-matches">
      <h2>Matches for {getTeamName(id)} Cricket Team</h2>

      <div className="tabs">
        {finalTabs.map((tab) => (
          <button
            key={tab.key}
            className={activeTab === tab.key ? 'active' : ''}
            onClick={() => setActiveTab(tab.key)}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <>
          {tabMatches.length === 0 && (
            <p>No {activeTab} matches available.</p>
          )}
          <div className="match-list">
            {tabMatches.map((match) => (
              <div
                key={match.id}
                className="match-card"
                onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Teams' } })}
              >
                <h4>{getTeamName(match.localteam_id)} vs {getTeamName(match.visitorteam_id)}, {match.round}</h4>
                {match.status !== 'NS' && <p>Status: {match.status}</p>}
                <p>Venue: {getVenueName(match.venue_id)}, {getVenueCity(match.venue_id)}</p>
                <CountdownTimer startTime={match.starting_at} />
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}
