// src/components/Series.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Series.css';

export default function Series() {
  const [leagues, setLeagues] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState(null);
  const [fixtures, setFixtures] = useState([]);
  const [filter, setFilter] = useState({ team: '', venue: '', season: '', tab: 'upcoming' });
  const [seasons, setSeasons] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('/api/leagues')
      .then(res => res.json())
      .then(data => setLeagues(data.data || []));
  }, []);

  const fetchFixtures = async (leagueId) => {
    setLoading(true);
    setSelectedLeague(leagueId);
    try {
      const res = await fetch(`/api/league/${leagueId}/fixtures`);
      const data = await res.json();
      setFixtures(data.fixtures || []);
      setSeasons(data.seasons || []);
    } catch (err) {
      console.error('❌ Error loading fixtures', err);
    } finally {
      setLoading(false);
    }
  };

  const groupedByDate = fixtures.reduce((acc, fix) => {
    const date = fix.starting_at.split('T')[0];
    if (!acc[date]) acc[date] = [];
    acc[date].push(fix);
    return acc;
  }, {});

  const filtered = Object.entries(groupedByDate).reduce((acc, [date, matches]) => {
    const filteredMatches = matches.filter(match => {
      const teamMatch = filter.team
        ? match.localteam.name.toLowerCase().includes(filter.team.toLowerCase()) ||
          match.visitorteam.name.toLowerCase().includes(filter.team.toLowerCase())
        : true;

      const venueMatch = filter.venue
        ? match.venue?.name.toLowerCase().includes(filter.venue.toLowerCase())
        : true;

      const seasonMatch = filter.season ? match.season_id === parseInt(filter.season) : true;
      const tabMatch = filter.tab === 'results' ? match.status === 'Finished' : match.status !== 'Finished';

      return teamMatch && venueMatch && seasonMatch && tabMatch;
    });

    if (filteredMatches.length > 0) acc[date] = filteredMatches;
    return acc;
  }, {});

  const handleExport = () => {
    const csvRows = [['Date', 'Match', 'Venue', 'Status']];
    Object.entries(filtered).forEach(([date, matches]) => {
      matches.forEach(m => {
        csvRows.push([
          date,
          `${m.localteam.name} vs ${m.visitorteam.name}`,
          m.venue?.name || 'N/A',
          m.status,
        ]);
      });
    });

    const csvContent = csvRows.map(r => r.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = `fixtures_${selectedLeague}.csv`;
    a.click();
  };

  return (
    <div className="series-container">
      <h2>{selectedLeague ? 'Fixtures' : 'All Series'}</h2>

      {!selectedLeague ? (
        <div className="league-grid">
          {leagues.map(l => (
            <div className="league-card" key={l.id} onClick={() => fetchFixtures(l.id)}>
              <h4>{l.name}</h4>
              <p>{l.type}</p>
            </div>
          ))}
        </div>
      ) : (
        <>
          <div className="filters">
            <button onClick={() => setSelectedLeague(null)}>← Back to Leagues</button>
            <input
              placeholder="Filter by team..."
              value={filter.team}
              onChange={e => setFilter({ ...filter, team: e.target.value })}
            />
            <input
              placeholder="Filter by venue..."
              value={filter.venue}
              onChange={e => setFilter({ ...filter, venue: e.target.value })}
            />
            <select
              value={filter.season}
              onChange={e => setFilter({ ...filter, season: e.target.value })}
            >
              <option value="">All Seasons</option>
              {seasons.map(s => (
                <option key={s.id} value={s.id}>{s.name}</option>
              ))}
            </select>
            <div className="tab-switch">
              <button className={filter.tab === 'upcoming' ? 'active' : ''} onClick={() => setFilter({ ...filter, tab: 'upcoming' })}>Upcoming</button>
              <button className={filter.tab === 'results' ? 'active' : ''} onClick={() => setFilter({ ...filter, tab: 'results' })}>Results</button>
            </div>
            <button onClick={handleExport}>Export CSV</button>
          </div>

          {loading ? (
            <p>Loading fixtures...</p>
          ) : (
            Object.entries(filtered).map(([date, matches]) => (
              <div key={date} className="fixture-group">
                <h4>{date}</h4>
                {matches.map(match => (
                  <Link key={match.id} to={`/match/${match.id}`} state={{ from: 'series', leagueId: selectedLeague }} className="match-card-link">
                    <div className="fixture-card" key={match.id}>
                      <strong>{match.localteam.name} vs {match.visitorteam.name}</strong>
                      <p>{match.venue?.name || 'Venue TBD'}</p>
                      <p>{match.status}</p>
                    </div>
                  </Link>
                ))}
              </div>
            ))
          )}
        </>
      )}
    </div>
  );
}
