import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match }) => {
  const { score } = match;

  if (!score || score.length === 0) {
    return <p>No Scorecard available for this match.</p>;
  }

  return (
    <div className="scorecard-tab">
      <h3>ðŸ“Š Innings Summary</h3>

      {score.map((inning, index) => (
        <div key={index} className="innings-block">
          <h4>{inning.inning}</h4>
          <p><strong>Batting:</strong> {inning.battingTeam}</p>
          <p><strong>Bowling:</strong> {inning.bowlingTeam}</p>
          <p><strong>Runs:</strong> {inning.runs}</p>
          <p><strong>Wickets:</strong> {inning.wickets}</p>
          <p><strong>Overs:</strong> {inning.overs}</p>
        </div>
      ))}
    </div>
  );
};

export default ScorecardTab;
