import React, { useEffect, useState } from 'react';
import {
  liveStatuses,
  completedMatchStatuses,
  upcomingMatchStatuses,
} from './../../constants/matchStatusConstants';
import { useSchedule } from './../../context/ScheduleContext';
import MatchCard from './../MatchCard';
import './css/FeaturedMatches.css';
import { useNavigate } from 'react-router-dom';
import dayjs from 'dayjs';

const FeaturedMatches = () => {
  const { matches, loading } = useSchedule();
  const [groupedMatches, setGroupedMatches] = useState({});
  const [activeSeries, setActiveSeries] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!matches || loading) return;

    const now = dayjs();

    const live = matches.filter(
      m => liveStatuses.includes(m.status)
    );

    const upcoming = matches
      .filter(m => upcomingMatchStatuses.includes(m.status))
      .sort((a, b) => new Date(a.starting_at) - new Date(b.starting_at))
      .slice(0, 10);

    const completed = matches
      .filter(m =>
        completedMatchStatuses.includes(m.status) &&
        dayjs(m.starting_at).isAfter(dayjs().subtract(2, 'day'))
      )
      .sort((a, b) => new Date(b.starting_at) - new Date(a.starting_at));

    const all = [...live, ...upcoming, ...completed];

    const grouped = {};
    all.forEach(match => {
      const key = match?.league?.name || 'Other';
      if (!grouped[key]) grouped[key] = [];
      grouped[key].push(match);
    });

    setGroupedMatches(grouped);

    if (!activeSeries) {
      const firstSeries = Object.keys(grouped)[0];
      setActiveSeries(firstSeries);
    }
  }, [matches, loading]);

  if (loading || !Object.keys(groupedMatches).length) {
    return <div className="loading-spinner">Loading matches...</div>;
  }

  const seriesKeys = Object.keys(groupedMatches);

  return (
    <div className="featured-container">
      <div className="series-scroll-bar">
        <button
          className={`series-tab ${!activeSeries ? 'active' : ''}`}
          onClick={() => setActiveSeries(null)}
        >
          Matches ({matches.length})
        </button>
        {seriesKeys.map(series => (
          <button
            key={series}
            className={`series-tab ${activeSeries === series ? 'active' : ''}`}
            onClick={() => setActiveSeries(series)}
          >
            {series}
          </button>
        ))}
        <button className="series-tab all-btn" onClick={() => navigate('/schedule')}>
          All
        </button>
      </div>

      <div className="match-cards-row">
        {(activeSeries
          ? groupedMatches[activeSeries] || []
          : Object.values(groupedMatches).flat()
        ).map(match => (
          <MatchCard key={match.id} match={match} />
        ))}
      </div>
    </div>
  );
};

export default FeaturedMatches;
