// PartnershipTab.js
import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentaryData }) => {
  if (!Array.isArray(commentaryData?.innings)) return <div>No data available</div>;

  const partnershipsByInning = [];

  commentaryData.innings.forEach((inning) => {
    const partnerships = [];
    let currentBatsmen = new Set();
    let currentPair = [];
    let runs = 0;
    let balls = 0;
    let extras = 0;

    const deliveries = [...inning.deliveries].reverse(); // From first ball to last

    deliveries.forEach((ball, idx) => {
      const { batsman, non_striker, total_run, extra_type, ball_number } = ball;

      const pair = [batsman, non_striker].sort().join(' & ');

      if (
        currentPair.length === 0 ||
        currentPair.join(' & ') !== [batsman, non_striker].sort().join(' & ')
      ) {
        if (currentPair.length === 2) {
          partnerships.push({
            pair: currentPair.join(' & '),
            runs,
            balls,
            extras,
          });
        }
        currentPair = [batsman, non_striker].sort();
        runs = 0;
        balls = 0;
        extras = 0;
      }

      if (extra_type) {
        extras += parseInt(total_run);
      } else {
        runs += parseInt(total_run);
      }
      balls++;

      // If this is the last delivery, push the current partnership
      if (idx === deliveries.length - 1 && currentPair.length === 2) {
        partnerships.push({
          pair: currentPair.join(' & '),
          runs,
          balls,
          extras,
        });
      }

      // If a wicket fell, continue to next partnership
      if (ball.wicket) {
        currentPair = [];
        runs = 0;
        balls = 0;
        extras = 0;
      }
    });

    partnershipsByInning.push(partnerships);
  });

  return (
    <div className="partnership-tab">
      {partnershipsByInning.map((inning, index) => (
        <div key={index} className="inning-block">
          <h3>Inning {index + 1}</h3>
          <table>
            <thead>
              <tr>
                <th>Batting Pair</th>
                <th>Runs</th>
                <th>Balls</th>
                <th>Extras</th>
              </tr>
            </thead>
            <tbody>
              {inning.map((partnership, i) => (
                <tr key={i}>
                  <td>{partnership.pair}</td>
                  <td>{partnership.runs}</td>
                  <td>{partnership.balls}</td>
                  <td>{partnership.extras}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default PartnershipTab;
