import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentaryData }) => {
  console.log(commentary);
  if (!commentary || commentary.length === 0) {
    return <p>No data available</p>;
  }

  const partnershipsByInning = [];

  commentary
    .sort((a, b) => a.inning - b.inning || a.over - b.over || a.ball - b.ball)
    .forEach((entry) => {
      const { inning, batsman, runs, extras, dismissal } = entry;
      partnershipsByInning[inning] = partnershipsByInning[inning] || {
        currentPair: [],
        list: [],
        balls: 0,
        runs: 0,
      };

      const inningData = partnershipsByInning[inning];

      // If batsman not already in pair, add them (up to 2)
      if (!inningData.currentPair.find((p) => p.id === batsman.id)) {
        if (inningData.currentPair.length === 2) {
          // Partnership ends, push and reset
          inningData.list.push({
            pair: [...inningData.currentPair],
            runs: inningData.runs,
            balls: inningData.balls,
          });

          // Remove dismissed batsman if possible
          const dismissed = dismissal ? batsman.id : null;
          const remaining = inningData.currentPair.find(p => p.id !== dismissed);
          inningData.currentPair = remaining ? [remaining] : [];
          inningData.runs = 0;
          inningData.balls = 0;
        }

        inningData.currentPair.push(batsman);
      }

      // Update partnership stats
      inningData.runs += runs + (extras || 0);
      inningData.balls += 1;

      // On dismissal, push partnership
      if (dismissal) {
        inningData.list.push({
          pair: [...inningData.currentPair],
          runs: inningData.runs,
          balls: inningData.balls,
        });

        // Keep only the surviving batsman
        const remaining = inningData.currentPair.find((p) => p.id !== batsman.id);
        inningData.currentPair = remaining ? [remaining] : [];
        inningData.runs = 0;
        inningData.balls = 0;
      }
    });

  // After loop, push any ongoing partnerships
  partnershipsByInning.forEach((inningData) => {
    if (inningData?.currentPair?.length === 2 && inningData.runs > 0) {
      inningData.list.push({
        pair: [...inningData.currentPair],
        runs: inningData.runs,
        balls: inningData.balls,
      });
    }
  });

  return (
    <div className="partnership-tab">
      {partnershipsByInning.map((inningData, inningIdx) => (
        <div key={inningIdx} className="inning-block">
          <h3>Inning {inningIdx + 1}</h3>
          {inningData.list.length === 0 ? (
            <p>No partnerships in this inning</p>
          ) : (
            <ul>
              {inningData.list.map((p, idx) => (
                <li key={idx}>
                  {p.pair.map((b) => b.name).join(' & ')} â€“ <strong>{p.runs} runs</strong> ({p.balls} balls)
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
};

export default PartnershipTab;
