// src/components/home/TopSeries.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './css/TopSeries.css'; // Create styling as needed
import dayjs from 'dayjs';

export default function TopSeries() {
  const [seriesList, setSeriesList] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('/api/series').then((res) => {
      const allSeries = res.data.data || [];
      const now = dayjs();

      const ongoing = allSeries.filter(s =>
        dayjs(s.start_date).isBefore(now) && dayjs(s.end_date).isAfter(now)
      );

      const upcoming = allSeries.filter(s =>
        dayjs(s.start_date).isAfter(now)
      );

      const topOngoing = ongoing.slice(0, 2);
      const topUpcoming = upcoming.slice(0, 4 - topOngoing.length);

      setSeriesList([...topOngoing, ...topUpcoming]);
    });
  }, []);

  return (
    <div className="top-series-widget">
      <h3>üèè Top Series</h3>
      {seriesList.map(series => (
        <div
          key={series.id}
          className="series-widget-card"
          onClick={() =>
            navigate(`/series/${series.id}/${series.season_id}`, {
              state: {
                seriesLabel: `${series.name} ${series.season}`,
                seriesCode: series.code,
              },
            })
          }
        >
          <h4>{series.name}</h4>
          <p>{series.season}</p>
          <p>{series.start_date} - {series.end_date}</p>
        </div>
      ))}
    </div>
  );
}
