import React from 'react';
import { Link } from 'react-router-dom';
import './FantasyTab.css';

const FantasyTab = ({ fantasyData }) => {
  if (!fantasyData || !fantasyData.innings || !fantasyData.totals) {
    return <p>No fantasy data available.</p>;
  }

  const renderPointsTable = (title, players) => (
    <div className="fantasy-section">
      <h3>{title}</h3>
      <table>
        <thead>
          <tr>
            <th>Player</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
          {players.map(player => (
            <tr key={player.id}>
              <td>
                <Link to={`/player/${player.id}`} className="player-link" target="_blank">
                  {player.name}
                </Link>
              </td>
              <td>{player.points}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="fantasy-tab">
      <h2>Fantasy Match Points</h2>

      {fantasyData.innings.map((inning, idx) => (
        <div key={idx} className="inning-block">
          <h3>{inning.inning}</h3>
          {renderPointsTable('Batting Points', inning.batting || [])}
          {renderPointsTable('Bowling Points', inning.bowling || [])}
          {renderPointsTable('Catching Points', inning.catching || [])}
        </div>
      ))}

      <div className="total-points">
        {renderPointsTable('Total Points (All Players)', fantasyData.totals)}
      </div>
    </div>
  );
};

export default FantasyTab;
