import React, { useEffect, useState, useRef } from 'react';
import {
  liveStatuses,
  completedMatchStatuses,
  upcomingMatchStatuses,
} from './../../constants/matchStatusConstants';
import { useSchedule } from './../../context/ScheduleContext';
import MatchCard from './../MatchCard';
import './css/FeaturedMatches.css';

const FeaturedMatches = () => {
  const { matches, loading } = useSchedule();
  const [liveMatches, setLiveMatches] = useState([]);
  const [upcomingMatches, setUpcomingMatches] = useState([]);
  const [completedMatches, setCompletedMatches] = useState([]);
  const [stagesMap, setStagesMap] = useState({});
  const [activeStage, setActiveStage] = useState(null);
  const scrollRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    if (!matches || loading) return;

    const live = matches.filter(m => liveStatuses.includes(m.status));
    const upcoming = matches.filter(m => upcomingMatchStatuses.includes(m.status));
    const completed = matches.filter(m => completedMatchStatuses.includes(m.status));

    setLiveMatches(live);
    setUpcomingMatches(upcoming);
    setCompletedMatches(completed);

    // Map matches by stage name
    const stageMap = {};
    [...live, ...upcoming, ...completed].forEach(match => {
      const stage = match.stage?.name || 'Unknown';
      if (!stageMap[stage]) stageMap[stage] = [];
      stageMap[stage].push(match);
    });
    setStagesMap(stageMap);

    // Set first active stage with matches
    const stageNames = Object.keys(stageMap);
    const firstStageWithMatches = stageNames.find(stage => stageMap[stage].length > 0);
    setActiveStage(firstStageWithMatches || stageNames[0] || null);
  }, [matches, loading]);

  const handleScroll = (e) => {
    const scrollLeft = e.target.scrollLeft;
    const containerWidth = e.target.offsetWidth;
    const page = Math.round(scrollLeft / containerWidth);
    setCurrentPage(page);
  };

  const totalPages = () => {
    if (!activeStage || !stagesMap[activeStage]) return 0;
    return Math.ceil(stagesMap[activeStage].length / 3); // 3 match cards per page
  };

  const renderMatches = () => {
    if (!activeStage || !stagesMap[activeStage]) return [];

    const matches = stagesMap[activeStage];
    return matches.map((match, index) => (
      <div className="match-card-wrapper" key={match.id}>
        <MatchCard match={match} />
      </div>
    ));
  };

  const totalMatchesCount = liveMatches.length + upcomingMatches.length + completedMatches.length;

  return (
    <div className="featured-matches-container">
      <div className="matches-header">
        <div className="match-tabs">
          <span className="tab active-tab">Matches ({totalMatchesCount})</span>
          {Object.entries(stagesMap).map(([stage, matches]) => (
            matches.length > 0 && (
              <span
                key={stage}
                className={`tab ${activeStage === stage ? 'active-tab' : ''}`}
                onClick={() => {
                  setActiveStage(stage);
                  setCurrentPage(0);
                  if (scrollRef.current) scrollRef.current.scrollLeft = 0;
                }}
              >
                {stage}
              </span>
            )
          ))}
        </div>
        <a href="/schedule" className="view-all-link">All</a>
      </div>

      <div
        className="matches-scroll-container"
        ref={scrollRef}
        onScroll={handleScroll}
      >
        <div className="matches-row">
          {renderMatches()}
        </div>
      </div>

      {totalPages() > 1 && (
        <div className="pagination-dots">
          {Array.from({ length: totalPages() }).map((_, idx) => (
            <span
              key={idx}
              className={`dot ${currentPage === idx ? 'active-dot' : ''}`}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default FeaturedMatches;
