import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match, commentary }) => {
  const scorecard = match?.scorecard;
  if (!scorecard) return <p>No scorecard data available.</p>;

  const formatDismissalText = (text = '') => {
    const capitalize = str =>
      str
        .split(' ')
        .map(w => w.charAt(0).toUpperCase() + w.slice(1))
        .join(' ');

    if (/^c and b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^c and b\s+([a-z\s]+)$/i, (_, name) => `c and b ${capitalize(name.trim())}`);
    }

    if (/^(c|st)\s+[a-z\s]+\s+b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^(c|st)\s+([a-z\s]+)\s+b\s+([a-z\s]+)$/i, (_, prefix, catcher, bowler) => {
        return `${prefix} ${capitalize(catcher.trim())} b ${capitalize(bowler.trim())}`;
      });
    }

    if (/^b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^b\s+([a-z\s]+)$/i, (_, bowler) => `b ${capitalize(bowler.trim())}`);
    }

    if (/^lbw\s+b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^lbw\s+b\s+([a-z\s]+)$/i, (_, bowler) => `lbw b ${capitalize(bowler.trim())}`);
    }

    return text;
  };

  const getFallOfWickets = (inningNum) => {
    if (!commentary) return [];

    const fow = [];
    let total = 0;
    let wickets = 0;

    console.log(commentary);

    commentary
      .filter(c => c.inning === inningNum && c.dismissal)
      .sort((a, b) => a.n - b.n)
      .forEach(entry => {
        total += entry.runs + (entry.extras || 0);
        wickets += 1;
        const batsman = entry.batsman?.name || 'Unknown';
        fow.push(`${batsman} ${total}/${wickets} (${entry.over}.${entry.ball})`);
      });

    return fow;
  };

  const getTotalScore = (inningName) => {
    const total = match?.score?.find(s => s.inning === inningName);
    if (!total) return '';
    return `${total.r}/${total.w} (${total.o} overs)`;
  };

  return (
    <div className="scorecard-tab">
      <h3>ðŸ“Š Innings Summary</h3>

      {scorecard.map((inning, index) => (
        <div key={index} className="innings-block">
          <h4>{inning.inning}</h4>

          <h5>Batting</h5>
          <table className="scorecard-table">
            <thead>
              <tr>
                <th>Batsman</th>
                <th>R</th>
                <th>B</th>
                <th>4s</th>
                <th>6s</th>
                <th>SR</th>
                <th>Dismissal</th>
              </tr>
            </thead>
            <tbody>
              {inning.batting.map((batter, i) => (
                <tr key={i}>
                  <td>{batter.batsman?.name}</td>
                  <td>{batter.r}</td>
                  <td>{batter.b}</td>
                  <td>{batter['4s']}</td>
                  <td>{batter['6s']}</td>
                  <td>{batter.sr}</td>
                  <td>{formatDismissalText(batter['dismissal-text']) || 'Not out'}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <p className="extras">
            <strong>Extras:</strong> {(inning.extras?.r || 0) + (inning.extras?.b || 0)} (Runs: {inning.extras?.r || 0}, Byes: {inning.extras?.b || 0})
          </p>

          <p className="totals">
            <strong>Total:</strong> {getTotalScore(inning.inning)}
          </p>

<h5>Fall of Wickets</h5>
<ul className="fall-of-wickets">
  {(() => {
    let totalRuns = 0;
    let fall = [];
    let wicketCount = 0;
    let dismissedSet = new Set();

    const inningBalls = match.commentary?.filter(c => c.inning === index) || [];
console.log(inningBalls);
    inningBalls.forEach(ball => {
      const isWicket = !!ball.dismissal;
      const runs = ball.runs || 0;
      const extras = ball.extras || 0;
      totalRuns += runs + extras;

      if (isWicket && ball.batsman && !dismissedSet.has(ball.batsman.id)) {
        wicketCount += 1;
        const over = `${ball.over}.${ball.ball}`;
        const name = ball.batsman.name;
        fall.push(`${totalRuns}/${wicketCount} (${name}, ${over} ov)`);
        dismissedSet.add(ball.batsman.id);
      }
    });

    return fall.length > 0 ? fall.map((f, i) => (
      <li key={i}>{f}</li>
    )) : <li>No wickets fallen</li>;
  })()}
</ul>

          <h5>Bowling</h5>
          <table className="scorecard-table">
            <thead>
              <tr>
                <th>Bowler</th>
                <th>O</th>
                <th>M</th>
                <th>R</th>
                <th>W</th>
                <th>ECO</th>
              </tr>
            </thead>
            <tbody>
              {inning.bowling.map((bowler, i) => (
                <tr key={i}>
                  <td>{bowler.bowler?.name}</td>
                  <td>{bowler.o}</td>
                  <td>{bowler.m}</td>
                  <td>{bowler.r}</td>
                  <td>{bowler.w}</td>
                  <td>{bowler.eco}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default ScorecardTab;
