import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import './TeamSeries.css';

const TeamSeries = () => {
  const { id } = useParams();
  const { teamName } = useParams();
  const [seriesList, setSeriesList] = useState([]);
  const [loading, setLoading] = useState(true);

  const location = useLocation();
  const { from } = location.state || {};

  const breadcrumbItems = [
    { label: 'Home', link: '/' },
    { label: 'Teams', link: '/teams' },
    { label: teamName, link: '/teams/${teamName}/series' },
  ];

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const res = await fetch(`/api/series?search=${encodeURIComponent(teamName)}`);
        const json = await res.json();
        setSeriesList(json.data || []);
      } catch (err) {
        console.error('Error fetching series:', err);
        setSeriesList([]);
      } finally {
        setLoading(false);
      }
    };

    fetchSeries();
  }, [teamName]);

  const getOrdinal = (n) => {
    const s = ["th", "st", "nd", "rd"],
      v = n % 100;
    return n + (s[(v - 20) % 10] || s[v] || s[0]);
  };

  const formatFullDate = (input, fallbackYear = null, startMonth = null) => {
    if (!input) return 'Invalid date';

    let date;

    if (/^\d{4}-\d{2}-\d{2}$/.test(input)) {
      date = new Date(input);
    } else if (/^[A-Za-z]{3,9} \d{1,2}$/.test(input) && fallbackYear !== null) {
      const tempDate = new Date(`${input} ${fallbackYear}`);
      const endMonth = tempDate.getMonth();
      const adjustedYear = (startMonth !== null && endMonth < startMonth) ? fallbackYear + 1 : fallbackYear;
      date = new Date(`${input} ${adjustedYear}`);
    } else {
      date = new Date(input);
    }

    if (isNaN(date)) return 'Invalid date';

    const day = getOrdinal(date.getDate());
    const month = date.toLocaleString('default', { month: 'long' });
    const year = date.getFullYear();

    return `${month} ${day}, ${year}`;
  };

  return (
    <div className="team-series-page">
      <h2>ðŸ“… {teamName} Cricket Team</h2>
      {loading ? (
        <p>Loading series...</p>
      ) : seriesList.length === 0 ? (
        <p>No series or matches found for {teamName} team.</p>
      ) : (
        <div className="series-grid">
          {seriesList.map((s) => (
            <div
              key={s.id}
              className="series-card"
              onClick={() => window.location.href = `/series/${s.id}`}
            >
              <h3>{s.name}</h3>
              <p>Start Date - {formatFullDate(s.startDate)}</p>
              <p>End Date - {formatFullDate(s.endDate, new Date(s.startDate).getFullYear(), new Date(s.startDate).getMonth())}</p>
              <p>TEST - {s.test}, ODI - {s.odi}, T20 - {s.t20}</p>
              <p>Total Matches - {(s.test || 0) + (s.odi || 0) + (s.t20 || 0)}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default TeamSeries;
