import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Series.css';

const Series = () => {
  const [allSeries, setAllSeries] = useState([]);
  const [activeTab, setActiveTab] = useState('upcoming');
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredSeries, setFilteredSeries] = useState([]);
  const [offset, setOffset] = useState(0);
  const [loading, setLoading] = useState(true);
  const PAGE_SIZE = 10;
const [activeSeriesTab, setActiveSeriesTab] = useState('upcoming');

  const navigate = useNavigate();

  const getOrdinal = (n) => {
    const s = ["th", "st", "nd", "rd"];
    const v = n % 100;
    return n + (s[(v - 20) % 10] || s[v] || s[0]);
  };

  const formatFullDate = (input, fallbackYear = null, startMonth = null) => {
    if (!input) return 'Invalid date';
    let date;
    if (/^\d{4}-\d{2}-\d{2}$/.test(input)) {
      date = new Date(input);
    } else if (/^[A-Za-z]{3,9} \d{1,2}$/.test(input) && fallbackYear !== null) {
      const tempDate = new Date(`${input} ${fallbackYear}`);
      const endMonth = tempDate.getMonth();
      const adjustedYear = (startMonth !== null && endMonth < startMonth) ? fallbackYear + 1 : fallbackYear;
      date = new Date(`${input} ${adjustedYear}`);
    } else {
      date = new Date(input);
    }
    if (isNaN(date)) return 'Invalid date';
    const day = getOrdinal(date.getDate());
    const month = date.toLocaleString('default', { month: 'long' });
    const year = date.getFullYear();
    return `${month} ${day}, ${year}`;
  };

  const fetchAllSeries = async (term = '', newOffset = 0) => {
    setLoading(true);
    try {
      const res = await fetch(`/api/series?search=${encodeURIComponent(term)}&offset=${newOffset}`);
      const json = await res.json();
      setAllSeries(json.data || []);
    } catch (err) {
      console.error('Error loading series list', err);
      setAllSeries([]);
    } finally {
      setLoading(false);
    }
  };

  const loadSeriesDetails = async (seriesId) => {
    setLoading(true);
    try {
      const res1 = await fetch(`/api/series/${seriesId}/matches`);
      const json1 = await res1.json();
      setMatches(json1.data?.matchList || []);
      setSeriesInfo(json1.data?.info || []);

      const res2 = await fetch(`/api/series/${seriesId}/points`);
      const json2 = await res2.json();
      setPointsTable(json2.data || []);
    } catch (err) {
      console.error('Error loading series details', err);
    } finally {
      setLoading(false);
    }
  };

  const filterSeries = () => {
    const now = new Date();
    const filtered = allSeries.filter((s) => {
      const start = new Date(s.startDate);
      const end = new Date(s.endDate || s.startDate);
  if (activeSeriesTab === 'upcoming') return start > now;
  if (activeSeriesTab === 'live') return start <= now && end >= now;
  if (activeSeriesTab === 'completed') return end < now;
    });

    const searched = filtered.filter((s) =>
      s.name?.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setFilteredSeries(searched);
    setOffset(0);
  };

  useEffect(() => {
    fetchAllSeries();
  }, []);

  useEffect(() => {
    filterSeries();
  }, [allSeries, searchTerm, activeTab]);

  const paginated = filteredSeries.slice(offset, offset + PAGE_SIZE);

  const handlePrev = () => {
    const newOffset = Math.max(offset - PAGE_SIZE, 0);
    setOffset(newOffset);
  };

  const handleNext = () => {
    const newOffset = offset + PAGE_SIZE;
    if (newOffset < filteredSeries.length) setOffset(newOffset);
  };

  return (
    <div className="series-container">
      <h2>All Series</h2>

      <div className="tabs">
        {['upcoming', 'live', 'completed'].map((tab) => (
          <button
            key={tab}
            onClick={() => {
              setActiveTab(tab);
              setSearchTerm('');
            }}
            className={activeTab === tab ? 'active' : ''}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      <input
        type="text"
        placeholder={`Search ${activeTab} series...`}
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="series-search"
      />

      {loading ? (
        <p>Loading series...</p>
      ) : filteredSeries.length === 0 ? (
        <p>No {activeTab} series found.</p>
      ) : (
        <>
          <div className="series-grid">
            {paginated.map((s) => (
              <div
                key={s.id}
                className="series-card"
                onClick={() => navigate(`/series/${s.id}`)}
              >
                <h3>{s.name}</h3>
                <p>Start Date - {formatFullDate(s.startDate)}</p>
                <p>End Date - {formatFullDate(
                  s.endDate,
                  new Date(s.startDate).getFullYear(),
                  new Date(s.startDate).getMonth()
                )}</p>
                <p>TEST - {s.test}, ODI - {s.odi}, T20 - {s.t20}</p>
                <p>Total Matches - {(s.test || 0) + (s.odi || 0) + (s.t20 || 0)}</p>
              </div>
            ))}
          </div>

          <div className="series-pagination">
            <button disabled={offset === 0} onClick={handlePrev}>← Prev</button>
            <button disabled={offset + PAGE_SIZE >= filteredSeries.length} onClick={handleNext}>Next →</button>
          </div>
        </>
      )}
    </div>
  );
};

export default Series;
