import React from 'react';
import { useNavigate } from 'react-router-dom';
import CountdownTimer from './CountdownTimer';
import NotificationButton from './NotificationButton';
import { getTeamRuns, venueName, getTossWinnerName, getTossDecision, matchStageName } from '../utils/matchUtils';
import {
  upcomingMatchStatuses,
  otherMatchStatuses,
} from './../constants/matchStatusConstants';
import './MatchCard.css';

export default function MatchCard({ match }) {
  const navigate = useNavigate();

return (
  <div className="match-card" onClick={() => navigate(`/match/${match.id}`)}>
    {/* Top Header */}
    <div className="match-header">
      <span className={`status-label ${match.status === 'LIVE' ? 'live' : match.status === 'NS' ? 'upcoming' : 'result'}`}>
        {match.status === 'NS' ? 'UPCOMING' : match.status}
      </span>
      <span className="dot"></span>
      <span>{matchStageName(match)}</span>
    </div>

    {/* Match Title and Venue */}
    <div className="match-subtitle">{match.round} Â· {venueName(match)}</div>

    {/* Teams and Scores */}
    <div className="team-block">
      <div className="team-row">
        <div className="team-name">
          <img src={match.localteam.image_path} alt="" className="flag-icon" />
          {match.localteam.name}
        </div>
        <div className="score">{getTeamRuns(match, match.localteam.id)}</div>
      </div>
      <div className="team-row">
        <div className="team-name">
          <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
          {match.visitorteam.name}
        </div>
        <div className="score">{getTeamRuns(match, match.visitorteam.id)}</div>
      </div>
    </div>

    {/* Match Note or Toss Info */}
    {match.note ? (
      <div className={`status-note ${match.status === 'LIVE' ? 'live' : match.status === 'NS' ? 'upcoming' : 'result'}`}>
        {match.note}
      </div>
    ) : (
      match.toss_won_team_id && match.elected && (
        <div className="status-note">
          {getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}
        </div>
      )
    )}

    {/* Footer Buttons */}
    <div className="card-footer">
      <div>Schedule</div>
      <div>Report</div>
      <div
        onClick={(e) => {
          e.stopPropagation();
          navigate(`/series/${match.stage_id}/${match.season_id}`, {
            state: {
              seriesLabel: matchStageName(match),
              seriesCode: match?.type,
            },
          });
        }}
      >
        Series
      </div>
    </div>
  </div>
);


}
