// src/components/LineupTab.js
import React from 'react';
import './LineupTab.css';

const LineupTab = ({ match }) => {
  const { team1, team2, team1PlayingXI, team2PlayingXI } = match;

  const renderPlayers = (players) => {
    return players.map((player) => (
      <li key={player.playerId}>
        {player.playerName}
        {player.isCaptain && ' (C)'}
        {player.isKeeper && ' (WK)'}
      </li>
    ));
  };

  if (!team1PlayingXI?.length && !team2PlayingXI?.length) {
    return <p>No lineup data available for this match.</p>;
  }

  return (
    <div className="lineup-tab">
      <div className="team-lineup">
        <h3>{team1?.name || 'Team 1'} Lineup</h3>
        <ul>{renderPlayers(team1PlayingXI)}</ul>
      </div>

      <div className="team-lineup">
        <h3>{team2?.name || 'Team 2'} Lineup</h3>
        <ul>{renderPlayers(team2PlayingXI)}</ul>
      </div>
    </div>
  );
};

export default LineupTab;
