import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './SeriesTeams.css';

export default function SeriesTeams() {
  const { id } = useParams();
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    fetch(`/api/series/${id}/teams`)
      .then(res => res.json())
      .then(data => setTeams(data.data || []))
      .catch(console.error);
  }, [id]);

  return (
    <div className="series-teams">
      <h2 className="section-title">ðŸ‘¥ Teams</h2>
      <ul className="team-list">
        {teams.map(team => (
          <li key={team.id} className="team-item">
            <img src={team.image_path} alt={team.name} className="team-logo" />
            <span>{team.name}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}
