import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from './CountdownTimer';
import './css/TeamMatches.css';

export default function TeamMatches() {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [teamNames, setTeamNames] = useState({});
  const [activeTab, setActiveTab] = useState('live');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        const allMatches = res.data.data.fixtures || [];
        setMatches(allMatches);

        // Extract all team IDs
        const teamIds = new Set();
        allMatches.forEach((m) => {
          teamIds.add(m.localteam_id);
          teamIds.add(m.visitorteam_id);
        });

        const names = {};
        await Promise.all([...teamIds].map(async (teamId) => {
          try {
            const teamRes = await axios.get(`/api/teams/${teamId}`);
            names[teamId] = teamRes.data.data.data.name;
          } catch (err) {
            names[teamId] = 'Unknown';
          }
        }));

        setTeamNames(names);
      } catch (err) {
        console.error('Error loading matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [id]);

  const getTeamName = (teamId) => teamNames[teamId] || 'TBD';

  const live = ['LIVE', '1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4', 'Tea Break', 'Lunch', 'Dinner', 'Innings Break', 'Int.', 'Delayed'];
  const upcoming = ['NS', 'Postp.'];
  const completed = ['Finished', 'Cancl', 'Aban.'];

  const liveMatches = matches.filter(m => live.includes(m.status));
  const upcomingMatches = matches.filter(m => upcoming.includes(m.status))
  const completedMatches = matches.filter(m => completed.includes(m.status));

  const filterMatches = (type) => {
    if (type === 'live') return liveMatches;
    if (type === 'upcoming') return upcomingMatches;
    if (type === 'completed') return completedMatches;
  };

  const formattedDate = (input) => {
    if (!input) return 'N/A';
    try {
      return new Date(input).toLocaleString();
    } catch (e) {
      return 'Invalid date';
    }
  };

  const tabMatches = filterMatches(activeTab);

  console.log(matches);

  return (
    <div className="team-matches">
      <h2>Matches for {getTeamName(id)} Cricket Team</h2>

      <div className="tabs">
        {['live', 'upcoming', 'completed'].map((tab) => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <>
          {tabMatches.length === 0 && activeTab === 'live' && (
            <p>No live matches available.</p>
          )}
          {tabMatches.length === 0 && activeTab === 'upcoming' && (
            <p>No upcoming matches available.</p>
          )}
          {tabMatches.length === 0 && activeTab === 'completed' && (
            <p>No completed matches available.</p>
          )}

          <div className="match-list">
            {tabMatches.map((match) => (
              <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Teams' } })}>
                <h4>{getTeamName(match.localteam_id)} vs {getTeamName(match.visitorteam_id)}, {match.round}</h4>
                {match.status !== 'NS' && (
                  <p>Status: {match.status}</p>
                )}
                <CountdownTimer startTime={match.starting_at} />
              </div>
            ))}
          </div>
        </>
      )}

    </div>
  );
}
