import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './MatchDetails.css';

const MatchDetails = () => {
  const { id } = useParams();
  const [matchData, setMatchData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const res = await fetch(`/api/match/${id}`);
        const json = await res.json();
        setMatchData(json.data);
      } catch (err) {
        console.error('Failed to fetch match details', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatchDetails();
  }, [id]);

  if (loading) return <div className="loading">Loading match details...</div>;
  if (!match) return <div className="error">Match details not available.</div>;

  const renderTab = () => {
    switch (tab) {
      case 'info':
        return <InfoTab match={matchData} />;
      case 'scorecard':
        return <ScorecardTab match={matchData} />;
      case 'lineup':
        return <LineupTab match={matchData} />;
      case 'partnerships':
        return <PartnershipsTab match={matchData} />;
      default:
        return null;
    }
  };

  const teamA = matchData.teams?.[0];
  const teamB = matchData.teams?.[1];

  return (
    <div className="match-details">
      <h2 className="match-heading">{teamA?.name} vs {teamB?.name}</h2>
      <p className="match-meta">
        <strong>Status:</strong> {match.status} <br />
        <strong>Venue:</strong> {match.venue || 'TBD'} <br />
        <strong>Start Time:</strong> {match.dateTimeGMT ? new Date(match.dateTimeGMT).toLocaleString() : 'N/A'}
      </p>

      {match.score && match.score.length > 0 && (
        <>
          <h3>Scorecard</h3>
          {match.score.map((inning, index) => (
            <div key={index} className="innings-block">
              <h4>{inning.inning} - {inning.runs}/{inning.wickets} in {inning.overs} overs</h4>
              <p><strong>Batting:</strong> {inning.battingTeam}</p>
              <p><strong>Bowling:</strong> {inning.bowlingTeam}</p>
            </div>
          ))}
        </>
      )}

      {match.toss && <p><strong>Toss:</strong> {match.toss}</p>}
      {match.umpires && <p><strong>Umpires:</strong> {match.umpires}</p>}
    </div>
  );
};

export default MatchDetails;
