import React, { useEffect, useRef } from 'react';
import './CommentaryTab.css';

const CommentaryTab = ({ commentary = [], scorecard = [] }) => {
  const containerRef = useRef(null);

  // Group commentary by innings and overs
  const groupedByInning = commentary.reduce((acc, entry) => {
    const { inning, over } = entry;
    if (!acc[inning]) acc[inning] = {};
    if (!acc[inning][over]) acc[inning][over] = [];
    acc[inning][over].push(entry);
    return acc;
  }, {});

  const capitalizeDismissal = (text = '') =>
    text
      .split(' ')
      .map(word => (word.length > 1 ? word[0].toUpperCase() + word.slice(1).toLowerCase() : word.toLowerCase()))
      .join(' ');

  const getOverSummary = (inningIndex, over) => {
    const inningData = scorecard[inningIndex];
    if (!inningData) return null;

    const batsmen = inningData.batting?.slice(-2) || [];
    const bowler = inningData.bowling?.[over % inningData.bowling.length];
    const totalRuns = groupedByInning[inningIndex][over]?.reduce((sum, b) => sum + (b.runs || 0), 0) || 0;

    return (
      <div className="over-summary">
        <div className="summary-circle">{totalRuns}</div>
        <div>
          <strong>Batsmen:</strong>{' '}
          {batsmen.map(b => `${b.batsman?.name || ''} (${b.r || 0}/${b.b || 0})`).join(', ')}
        </div>
        <div><strong>Bowler:</strong> {bowler?.bowler?.name || 'Unknown'}</div>
      </div>
    );
  };

  // Scroll to top (latest inning)
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = 0;
    }
  }, []);

  const renderBall = (ball, idx) => {
    const batsman = ball.batsman?.name || 'Unknown';
    const bowler = ball.bowler?.name || 'Unknown';
    const emoji = ball.dismissal ? '‚úã' : ball.runs >= 4 ? 'üèè' : '';
    const text = ball.dismissal
      ? `OUT ‚Äì ${capitalizeDismissal(ball.dismissal)}`
      : `${ball.runs} run${ball.runs !== 1 ? 's' : ''}${ball.extras ? ` + ${ball.extras} extras` : ''}`;

    return (
      <div key={idx} className={`commentary-entry ${ball.dismissal ? 'dismissal' : ''}`}>
        <div className="overs">Over {ball.over}.{ball.ball}</div>
        <div className="details">
          {emoji} <strong>{bowler}</strong> <em>to</em> <strong>{batsman}</strong> ‚Äì {text}
        </div>
      </div>
    );
  };

  return (
    <div className="commentary-tab" ref={containerRef}>
      {Object.keys(groupedByInning)
        .sort((a, b) => b - a)
        .map(inning => (
          <div key={inning} className="inning-block">
            <h3>Inning {parseInt(inning) + 1}</h3>
            {Object.keys(groupedByInning[inning])
              .sort((a, b) => b - a)
              .map(over => (
                <div key={over} className="over-block">
                  {groupedByInning[inning][over]
                    .slice()
                    .reverse()
                    .map((ball, idx) => renderBall(ball, idx))}
                  <div className="over-progress">
                    {groupedByInning[inning][over].map((b, idx) => (
                      <span
                        key={idx}
                        className={`ball ${b.runs === 0 ? 'dot' : b.runs >= 4 ? 'boundary' : 'run'}`}
                      >
                        {b.runs || (b.extras ? 'E' : '‚Ä¢')}
                      </span>
                    ))}
                  </div>
                  {getOverSummary(inning, over)}
                </div>
              ))}
          </div>
        ))}
    </div>
  );
};

export default CommentaryTab;
