import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './css/PlayerDetails.css';

export default function PlayerDetails() {
  const { id } = useParams();
  const [player, setPlayer] = useState(null);
  const [countryMap, setCountryMap] = useState({});

  useEffect(() => {
    fetch(`/api/player/${id}`)
      .then(res => res.json())
      .then(data => setPlayer(data.data));

    fetch('/api/countries')
      .then(res => res.json())
      .then(data => {
        const map = {};
        data.data.forEach(c => {
          map[c.id] = c.name;
        });
        setCountryMap(map);
      });
  }, [id]);

  if (!player) return <div className="player-details">Loading player info...</div>;
console.log(player);
  return (
    <div className="player-details">
      <h2>{player.fullname}</h2>
      <img src={player.image_path} alt={player.fullname} className="player-detail-img" />
      <p><strong>Country:</strong> {countryMap[player.country_id]}</p>
      <p><strong>Date of Birth:</strong> {player.dateofbirth}</p>
      <p><strong>Gender:</strong> {player.gender === 'm' ? 'Male' : 'Female'}</p>
      <p><strong>Batting Style:</strong> {player.battingstyle || 'N/A'}</p>
      <p><strong>Bowling Style:</strong> {player.bowlingstyle || 'N/A'}</p>
      <p><strong>Position:</strong> {player.position?.name || 'N/A'}</p>
    </div>
  );
}
