// src/components/InfoTab.js
import React from 'react';
import './InfoTab.css';

const InfoTab = ({ match }) => {
  if (!match) return null;

  const {
    leagueName,
    matchType,
    tossWinner,
    tossDecision,
    matchDate,
    venue,
    umpires,
    tvUmpire,
    referee,
    note,
    round,
  } = match;

  const formattedDate = matchDate
    ? new Date(matchDate).toLocaleString()
    : 'N/A';

  return (
    <div className="info-tab">
      <table className="info-table">
        <tbody>
          {round && (
            <tr>
              <td><strong>Round</strong></td>
              <td>{round}</td>
            </tr>
          )}
          {leagueName && (
            <tr>
              <td><strong>League</strong></td>
              <td>{leagueName}</td>
            </tr>
          )}
          {matchType && (
            <tr>
              <td><strong>Match Type</strong></td>
              <td>{matchType}</td>
            </tr>
          )}
          {formattedDate && (
            <tr>
              <td><strong>Date</strong></td>
              <td>{formattedDate}</td>
            </tr>
          )}
          {venue && (
            <tr>
              <td><strong>Venue</strong></td>
              <td>{venue}</td>
            </tr>
          )}
          {(tossWinner || tossDecision) && (
            <tr>
              <td><strong>Toss</strong></td>
              <td>{tossWinner} elected to {tossDecision}</td>
            </tr>
          )}
          {(umpires?.length > 0 || tvUmpire || referee) && (
            <tr>
              <td><strong>Officials</strong></td>
              <td>
                {umpires?.map((u, i) => <span key={i}>{u} (On-field)<br /></span>)}
                {tvUmpire && <span>{tvUmpire} (TV)<br /></span>}
                {referee && <span>{referee} (Referee)</span>}
              </td>
            </tr>
          )}
          {note && (
            <tr>
              <td><strong>Note</strong></td>
              <td>{note}</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default InfoTab;
