import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Players.css';

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [displayedPlayers, setDisplayedPlayers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [countries, setCountries] = useState({});
  const [positions, setPositions] = useState({});
  const [loading, setLoading] = useState(true);
  const [limit, setLimit] = useState(10);
  const searchTimeout = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      console.log('📡 Fetching players, countries, positions...');
      try {
        const [playersRes, countriesRes, positionsRes] = await Promise.all([
          axios.get('/api/players'),
          axios.get('/api/countries'),
          axios.get('/api/positions'),
        ]);

        const countryMap = {};
        countriesRes.data.forEach(c => countryMap[c.id] = c);
        setCountries(countryMap);

        const positionMap = {};
        positionsRes.data.forEach(p => positionMap[p.id] = p.name);
        setPositions(positionMap);

        const allPlayers = playersRes.data.data || [];
        setPlayers(allPlayers);
        setDisplayedPlayers(allPlayers.slice(0, limit));
      } catch (err) {
        console.error('❌ Error loading players:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleLoadMore = () => {
    const next = players.slice(displayedPlayers.length, displayedPlayers.length + 10);
    setDisplayedPlayers(prev => [...prev, ...next]);
  };

  const handleSearchChange = (e) => {
    const val = e.target.value;
    setSearchQuery(val);

    clearTimeout(searchTimeout.current);
    if (!val.trim()) {
      setSuggestions([]);
      return;
    }

    searchTimeout.current = setTimeout(async () => {
      try {
        const res = await axios.get('/api/players-search', {
          params: { q: val, page: 1, perPage: 10 }
        });
        setSuggestions(res.data.data || []);
      } catch (err) {
        console.error('❌ Search failed', err);
      }
    }, 300);
  };

  const handleSuggestionClick = (player) => {
    setSelectedPlayer(player);
    setSearchQuery('');
    setSuggestions([]);
  };
console.log(suggestions);
  const closeModal = () => {
    setSelectedPlayer(null);
  };

  return (
    <div className="players-container">
      <h2>All Players</h2>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search players..."
        className="search-bar"
      />
      {suggestions.length > 0 && (
        <ul className="suggestions-list">
          {suggestions.map(p => (
            <li key={p.id} onClick={() => handleSuggestionClick(p)}>
              {p.fullname}
            </li>
          ))}
        </ul>
      )}

      {loading ? (
        <p>Loading players...</p>
      ) : (
        <div className="players-grid">
          {displayedPlayers.map(player => {
            const country = countries[player.country_id];
            const position = positions[player.position?.id];
            return (
              <div key={player.id} className="player-card" onClick={() => setSelectedPlayer(player)}>
                <img src={player.image_path} alt={player.fullname} className="player-img" />
                <div className="player-info">
                  <h4>{player.fullname}</h4>
                  {position && <p>{position}</p>}
                  {country && (
                    <div className="flag-info">
                      <img src={country.image_path} alt={country.name} className="flag-icon" />
                      <span>{country.name}</span>
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      )}

      {!loading && displayedPlayers.length < players.length && (
        <button className="load-more-btn" onClick={handleLoadMore}>
          Load More
        </button>
      )}

      {selectedPlayer && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            <button className="close-btn" onClick={closeModal}>✖</button>
            <h3>{selectedPlayer.fullname}</h3>
            <img src={selectedPlayer.image_path} alt={selectedPlayer.fullname} />
            <p><strong>Born:</strong> {selectedPlayer.dateofbirth}</p>
            <p><strong>Gender:</strong> {selectedPlayer.gender === 'm' ? 'Male' : 'Female'}</p>
            <p><strong>Batting Style:</strong> {selectedPlayer.battingstyle}</p>
            <p><strong>Bowling Style:</strong> {selectedPlayer.bowlingstyle}</p>
            <p><strong>Position:</strong> {positions[selectedPlayer.position?.id]}</p>
            <p><strong>Country:</strong> {countries[selectedPlayer.country_id]?.name}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Players;
