import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './css/TopPlayers.css';

export default function TopPlayers() {
  const [players, setPlayers] = useState([]);
  const [imageMap, setImageMap] = useState({});

  useEffect(() => {
    const loadTopPlayers = async () => {
      try {
        // Get list of top player names
        const res = await fetch('/api/default-player-images');
        const text = await res.text();
        const names = text
          .split('\n')
          .map((name) => name.trim())
          .filter(Boolean);

        // Get image mapping from Sportmonks
        const imageMapRes = await fetch('/api/player-image-map');
        const map = await imageMapRes.json();

        // Combine name and image
        const topPlayers = names.map((name) => ({
          name,
          image: map[name] || 'https://em-content.zobj.net/source/apple/354/bust-in-silhouette_1f464.png',
        }));

        setPlayers(topPlayers);
      } catch (err) {
        console.error('Error loading top players widget:', err);
      }
    };

    loadTopPlayers();
  }, []);

  return (
    <div className="top-players-section">
      <h3>Top Players</h3>
      <div className="top-players-grid">
        {players.map((p) => (
          <Link key={p.name} to={`/player/name/${encodeURIComponent(p.name)}`} className="top-player-card">
            <img src={p.image} alt={p.name} />
            <p>{p.name}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
