import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './SeriesMatches.css';

export default function SeriesMatches() {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch(`/api/series/${id}/matches`)
      .then(res => res.json())
      .then(data => setMatches(data.data || []));
  }, [id]);

  return (
    <div className="series-container">
      <h2 className="section-title">Matches</h2>
      {matches.length === 0 ? (
        <div className="no-data">No matches found.</div>
      ) : (
        matches.map(match => (
          <div className="series-card" key={match.id}>
            <h3>{match.localteam?.name} vs {match.visitorteam?.name}</h3>
            <p>{match.venue?.name} - {match.starting_at?.split('T')[0]}</p>
          </div>
        ))
      )}
    </div>
  );
}
