import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';

export default function SeriesMatchesTab({ seriesId }) {
  const [matches, setMatches] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`/api/series/${seriesId}/matches`).then(res => {
      setMatches(res.data.data || []);
    });
  }, [seriesId]);

console.log(matches);

  return (
    <div className="match-cards-row">
      {matches.map(match => (
        <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Series' } })}>

        <div style={{ marginTop: '10px' }}>
          {match.status !== 'Finished' &&
            <NotificationButton match={match} />
          }
        </div>

          {match.status === 'NS' && (
            <CountdownTimer startTime={match.starting_at} />
          )}
        </div>
      ))}
    </div>
  );
}
