import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PlayerDetails = () => {
  const { id } = useParams();
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    const fetchPlayer = async () => {
      const res = await fetch(`/api/players/${id}`);
      const json = await res.json();
      setPlayer(json.data);
    };
    fetchPlayer();
  }, [id]);

  if (!player) return <p>Loading player details...</p>;

  return (
    <div>
      <h2>{player.name}</h2>
      <img src={player.image} alt={player.name} style={{ width: '200px' }} />
      <p><strong>Country:</strong> {player.country}</p>
      <p><strong>Batting Style:</strong> {player.battingStyle}</p>
      <p><strong>Bowling Style:</strong> {player.bowlingStyle}</p>
      <p><strong>Position:</strong> {player.position}</p>
      <p><strong>Date of Birth:</strong> {player.dob}</p>

      <h3>Career Stats</h3>
      {player.career?.map((entry, index) => (
        <div key={index} style={{ marginBottom: '10px' }}>
          <h4>{entry.type}</h4>
          {entry.batting && (
            <ul>
              <li>Matches: {entry.batting.matches}</li>
              <li>Runs: {entry.batting.runs_scored}</li>
              <li>Innings: {entry.batting.innings}</li>
              <li>Average: {entry.batting.average}</li>
              <li>Strike Rate: {entry.batting.strike_rate}</li>
              <li>Fours: {entry.batting.four_x}</li>
              <li>Sixes: {entry.batting.six_x}</li>
              <li>50s: {entry.batting.fifties}, 100s: {entry.batting.hundreds}</li>
            </ul>
          )}
        </div>
      ))}
    </div>
  );
};

export default PlayerDetails;
