import React, { useEffect, useState } from 'react';
import { useSeriesMatches } from '../SeriesMatchesContext';
import axios from 'axios';

export default function SeriesSquadsTab({ seriesId }) {
  const [teams, setTeams] = useState([]);
  const { matches, loading: matchesLoading } = useSeriesMatches();
  const [loading, setLoading] = useState(true);

console.log(matches);

  useEffect(() => {
    if (matchesLoading || !matches.length) return;

    let isMounted = true;
    const seenTeams = new Map();

    async function fetchSquads() {
      try {
        for (const match of matches) {
          const seasonId = match.season_id;
          const teamsToFetch = [match.localteam, match.visitorteam];

          for (const team of teamsToFetch) {
            if (!seenTeams.has(team.id)) {
              try {
                const squadRes = await axios.get(`/api/teams/${team.id}/squad/${seasonId}`);
                seenTeams.set(team.id, {
                  ...team,
                  squad: squadRes.data.data || [],
                });
              } catch {
                seenTeams.set(team.id, {
                  ...team,
                  squad: [],
                });
              }
            }
          }
        }
        if (isMounted) {
          setTeams(Array.from(seenTeams.values()));
        }
      } catch (err) {
        console.error('Error loading squads', err);
      } finally {
        setLoading(false);
      }
    }

    fetchSquads();
    return () => { isMounted = false; };
  }, [seriesId, matches, matchesLoading]);

  return (
    <div>
      {loading ? (
        <p>Loading Squads...</p>
      ) : teams.map((team) => (
        <div key={team.id} style={{ marginBottom: '20px' }}>
          <h4>{team.name}</h4>
          {team.squad.squad?.map((player) => (
            <div key={player.id}>â€“ {player.fullname}</div>
          ))}
        </div>
      ))}
    </div>
  );
}
