import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match, commentary }) => {
  const scorecard = match?.scorecard;
  if (!scorecard) return <p>No scorecard data available.</p>;

  const formatDismissalText = (text = '') => {
    const capitalize = str =>
      str
        .split(' ')
        .map(w => w.charAt(0).toUpperCase() + w.slice(1))
        .join(' ');

    if (/^c and b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^c and b\s+([a-z\s]+)$/i, (_, name) => `c and b ${capitalize(name.trim())}`);
    }

    if (/^(c|st)\s+[a-z\s]+\s+b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^(c|st)\s+([a-z\s]+)\s+b\s+([a-z\s]+)$/i, (_, prefix, catcher, bowler) => {
        return `${prefix} ${capitalize(catcher.trim())} b ${capitalize(bowler.trim())}`;
      });
    }

    if (/^b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^b\s+([a-z\s]+)$/i, (_, bowler) => `b ${capitalize(bowler.trim())}`);
    }

    if (/^lbw\s+b\s+[a-z\s]+$/i.test(text)) {
      return text.replace(/^lbw\s+b\s+([a-z\s]+)$/i, (_, bowler) => `lbw b ${capitalize(bowler.trim())}`);
    }

    return text;
  };

  const getFallOfWickets = (inningNum) => {
    if (!commentary) return [];

    const fow = [];
    let total = 0;
    let wickets = 0;

    console.log(commentary);

    commentary
      .filter(c => c.inning === inningNum && c.dismissal)
      .sort((a, b) => a.n - b.n)
      .forEach(entry => {
        total += entry.runs + (entry.extras || 0);
        wickets += 1;
        const batsman = entry.batsman?.name || 'Unknown';
        fow.push(`${batsman} ${total}/${wickets} (${entry.over}.${entry.ball})`);
      });

    return fow;
  };

  const getTotalScore = (inningName) => {
    const total = match?.score?.find(s => s.inning === inningName);
    if (!total) return '';
    return `${total.r}/${total.w} (${total.o} overs)`;
  };

  return (
    <div className="scorecard-tab">
      <h3>ðŸ“Š Innings Summary</h3>

      {scorecard.map((inning, index) => (
        <div key={index} className="innings-block">
          <h4>{inning.inning}</h4>

          <h5>Batting</h5>
          <table className="scorecard-table">
            <thead>
              <tr>
                <th>Batsman</th>
                <th>R</th>
                <th>B</th>
                <th>4s</th>
                <th>6s</th>
                <th>SR</th>
                <th>Dismissal</th>
              </tr>
            </thead>
            <tbody>
              {inning.batting.map((batter, i) => (
                <tr key={i}>
                  <td>{batter.batsman?.name}</td>
                  <td>{batter.r}</td>
                  <td>{batter.b}</td>
                  <td>{batter['4s']}</td>
                  <td>{batter['6s']}</td>
                  <td>{batter.sr}</td>
                  <td>{formatDismissalText(batter['dismissal-text']) || 'Not out'}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <p className="extras">
            <strong>Extras:</strong> {(inning.extras?.r || 0) + (inning.extras?.b || 0)} (Runs: {inning.extras?.r || 0}, Byes: {inning.extras?.b || 0})
          </p>

          <p className="totals">
            <strong>Total:</strong> {getTotalScore(inning.inning)}
          </p>


{match.commentary && (
  <div className="fall-of-wickets-block">
    <h5>Fall of Wickets</h5>
    {match.scorecard.map((inning, idx) => {
      const inningName = inning.inning;

      // Filter commentary for current inning
      const thisInningBalls = match.commentary.filter(
        ball => ball.inning === inningName
      );

      // Sort balls (descending or ascending as needed)
      const sortedBalls = [...thisInningBalls].sort((a, b) => parseFloat(a.ball) - parseFloat(b.ball));

      const wickets = [];
      const seenBalls = new Set();
      let totalRuns = 0;
      let wicketCount = 0;

      for (const ball of sortedBalls) {
        const ballKey = `${ball.ball}-${ball.wicket?.player_out}`;

        // Ensure no duplicates for same dismissal
        if (
          ball.wicket &&
          ball.wicket.player_out &&
          !seenBalls.has(ballKey)
        ) {
          seenBalls.add(ballKey);
          wicketCount++;

          wickets.push({
            ball: ball.ball,
            over: ball.over || ball.ball,
            player: ball.wicket.player_out,
            score: ball.score || totalRuns,
            number: wicketCount,
          });
        }

        if (ball.runs) totalRuns += ball.runs;
      }

      return (
        <div key={idx} className="fow-inning-block">
          <h6>{inningName}</h6>
          {wickets.length === 0 ? (
            <p>No wickets fallen.</p>
          ) : (
            <p className="fow-line">
              {wickets.map((wkt, i) => (
                <span key={i}>
                  <strong>{wkt.score}/{wkt.number}</strong> ({wkt.player}, {wkt.over} ov)
                  {i < wickets.length - 1 && ' Â· '}
                </span>
              ))}
            </p>
          )}
        </div>
      );
    })}
  </div>
)}


          <h5>Bowling</h5>
          <table className="scorecard-table">
            <thead>
              <tr>
                <th>Bowler</th>
                <th>O</th>
                <th>M</th>
                <th>R</th>
                <th>W</th>
                <th>ECO</th>
              </tr>
            </thead>
            <tbody>
              {inning.bowling.map((bowler, i) => (
                <tr key={i}>
                  <td>{bowler.bowler?.name}</td>
                  <td>{bowler.o}</td>
                  <td>{bowler.m}</td>
                  <td>{bowler.r}</td>
                  <td>{bowler.w}</td>
                  <td>{bowler.eco}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default ScorecardTab;
