import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './css/Players.css';

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [query, setQuery] = useState('');
  const [offset, setOffset] = useState(0);
  const [images, setImages] = useState({});

  const load = async () => {
    const url = query
      ? `/api/players?search=${encodeURIComponent(query)}&offset=0`
      : `/api/players?offset=${offset}`;
    const res = await fetch(url);
    const data = await res.json();
    const playerList = data?.data || [];
    setPlayers(playerList);

    // Fetch Sportmonks images for each player in parallel
    const imagePromises = playerList.map(async (p) => {
      const resp = await fetch(`/api/player-image/${encodeURIComponent(p.name)}`);
      const imgData = await resp.json();
      return { id: p.id, image: imgData.image };
    });

    const imageResults = await Promise.all(imagePromises);
    const imageMap = {};
    imageResults.forEach((img) => {
      imageMap[img.id] = img.image;
    });
    setImages(imageMap);
  };

  useEffect(() => { load(); }, [query, offset]);

  return (
    <div className="players-container">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search players..."
        value={query}
        onChange={e => setQuery(e.target.value)}
      />

      <div className="player-grid">
        {players.map((p) => (
          <Link key={p.id} to={`/player/${p.id}`} className="player-card">
            <img
              src={images[p.id] || 'https://via.placeholder.com/100x100?text=Player'}
              alt={p.name}
              className="player-image"
            />
            <h4>{p.name}</h4>
            <p>{p.country}</p>
          </Link>
        ))}
      </div>

      {!query && (
        <div className="paging">
          <button onClick={() => setOffset(o => Math.max(o - 50, 0))} disabled={offset === 0}>Prev</button>
          <button onClick={() => setOffset(o => o + 50)}>Next</button>
        </div>
      )}
    </div>
  );
}
