import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match }) => {
  const { batting = [], bowling = [], scoreboards = [], lineup = [] } = match.match || {};

  const getPlayerName = (id) => {
    const playerObj = lineup.find(p => p.player_id === id);
    return playerObj?.player?.fullname || 'N/A';
  };

  const getDismissal = (batsman) => {
    if (!batsman.wicket_id) return 'Not Out';
    const fielder = batsman.catch_stump_player_id ? getPlayerName(batsman.catch_stump_player_id) : '';
    const bowler = batsman.bowling_player_id ? getPlayerName(batsman.bowling_player_id) : '';
    return `${fielder ? `c ${fielder}` : ''} ${bowler ? `b ${bowler}` : ''}`.trim();
  };

  const groupByInnings = (data) => {
    return data.reduce((acc, item) => {
      const key = item.scoreboard;
      if (!acc[key]) acc[key] = [];
      acc[key].push(item);
      return acc;
    }, {});
  };

  const inningsBatting = groupByInnings(batting);
  const inningsBowling = groupByInnings(bowling);
  const inningsScoreboards = groupByInnings(scoreboards);

  const getExtras = (extras) => {
    if (!extras) return '0';
    const { wide = 0, noball_runs = 0, bye = 0, leg_bye = 0 } = extras;
    const total = wide + noball_runs + bye + leg_bye;
    return `${total} (${wide}w, ${noball_runs}nb, ${bye}b, ${leg_bye}lb)`;
  };

  const renderInnings = (key, index) => {
    const battingData = inningsBatting[key] || [];
    const bowlingData = inningsBowling[key] || [];
    const extrasData = inningsScoreboards[key]?.find(s => s.type === 'extra');
    const totalData = inningsScoreboards[key]?.find(s => s.type === 'total');
    const teamName = match.match.runs?.find(r => r.inning === index + 1)?.team?.name || `Innings ${index + 1}`;

    return (
      <div key={key} className="innings">
        <h3>{index + 1 === 1 ? '1st Innings' : '2nd Innings'} - {teamName}</h3>
        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Batsman</th>
              <th>R</th>
              <th>B</th>
              <th>4s</th>
              <th>6s</th>
              <th>SR</th>
              <th>Dismissal</th>
            </tr>
          </thead>
          <tbody>
            {battingData.map((batsman, i) => (
              <tr key={i}>
                <td>{getPlayerName(batsman.player_id)}</td>
                <td>{batsman.score}</td>
                <td>{batsman.ball}</td>
                <td>{batsman.four_x}</td>
                <td>{batsman.six_x}</td>
                <td>{batsman.rate}</td>
                <td>{getDismissal(batsman)}</td>
              </tr>
            ))}
            <tr>
              <td><strong>Extras</strong></td>
              <td colSpan="6">{getExtras(extrasData)}</td>
            </tr>
            <tr>
              <td><strong>Total</strong></td>
              <td colSpan="6">
                {totalData ? `${totalData.total}/${totalData.wickets} (${totalData.overs} ov)` : 'N/A'}
              </td>
            </tr>
          </tbody>
        </table>

        {battingData.some(b => b.fow_score && b.fow_balls) && (
          <div className="fow-section">
            <h4>Fall of Wickets</h4>
            <p>
              {battingData
                .filter(b => b.fow_score && b.fow_balls)
                .map(b => `${b.fow_score} (${getPlayerName(b.player_id)}, ${b.fow_balls} ov)`)
                .join(', ')}
            </p>
          </div>
        )}

        <h4>Bowling</h4>
        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Bowler</th>
              <th>O</th>
              <th>R</th>
              <th>W</th>
              <th>NB</th>
              <th>WD</th>
              <th>Econ</th>
            </tr>
          </thead>
          <tbody>
            {bowlingData.map((bowler, i) => (
              <tr key={i}>
                <td>{getPlayerName(bowler.player_id)}</td>
                <td>{bowler.overs}</td>
                <td>{bowler.runs}</td>
                <td>{bowler.wickets}</td>
                <td>{bowler.noball}</td>
                <td>{bowler.wide}</td>
                <td>{bowler.rate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="scorecard-tab">
      {Object.keys(inningsBatting).sort().map((key, i) => renderInnings(key, i))}
    </div>
  );
};

export default ScorecardTab;
