import React from 'react';
import './CommentaryTab.css';

const CommentaryTab = ({ commentary }) => {
  if (!commentary || commentary.length === 0) return <p>No commentary available.</p>;

  const capitalizeDismissal = (text) => {
    return text
      .split(' ')
      .map(word =>
        word.length > 1 ? word.charAt(0).toUpperCase() + word.slice(1).toLowerCase() : word.toLowerCase()
      )
      .join(' ');
  };

  const groupedByOver = {};

  [...commentary].reverse().forEach(entry => {
    const over = entry.over;
    if (!groupedByOver[over]) groupedByOver[over] = [];
    groupedByOver[over].push(entry);
  });

  return (
    <div className="commentary-list">
      {Object.entries(groupedByOver).map(([over, balls], i) => (
        <div key={i} className="over-group">
          <div className="over-header">Over {over}</div>
          {balls.map((entry, idx) => {
            const batsman = entry.batsman?.name || 'Unknown Batsman';
            const bowler = entry.bowler?.name || 'Unknown Bowler';
            const overBall = `${entry.over}.${entry.ball}`;
            const runs = entry.runs || 0;
            const extras = entry.extras || 0;
            const dismissal = entry.dismissal ? capitalizeDismissal(entry.dismissal) : null;

            const isSpecialDismissal = dismissal?.includes('Run Out') || dismissal?.includes('Retired');

            return (
              <div key={idx} className="commentary-entry">
                <div className="overs">Ball {overBall}</div>
                <div className="details">
                  <strong>{bowler}</strong> <em>to</em> <strong>{batsman}</strong> –{' '}
                  {dismissal ? (
                    <span className={`dismissal-text ${isSpecialDismissal ? 'special-dismissal' : ''}`}>
                      OUT – {dismissal}
                    </span>
                  ) : (
                    <>
                      <span className="runs-text">{runs} run{runs !== 1 ? 's' : ''}</span>
                      {extras > 0 && (
                        <span className="extras-text"> + {extras} extra{extras !== 1 ? 's' : ''}</span>
                      )}
                    </>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );
};

export default CommentaryTab;
