// src/pages/Series.js
import React, { useEffect, useState } from 'react';
import { useParams, useLocation, useNavigate, Link } from 'react-router-dom';
import './Series.css';

const Series = () => {
  const { id } = useParams(); // from /series/:id
  const [seriesList, setSeriesList] = useState([]);
  const [matches, setMatches] = useState([]);
  const [pointsTable, setPointsTable] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('fixtures');
  const navigate = useNavigate();
  const location = useLocation();
  const fromHome = location.state?.fromHome;

  useEffect(() => {
    if (!id) {
      const controller = new AbortController();
      const delayDebounce = setTimeout(() => {
        fetch(`/api/series?search=${encodeURIComponent(searchTerm)}`, { signal: controller.signal })
          .then(res => res.json())
          .then(json => setSeriesList(json.data || []))
          .catch(err => {
            if (err.name !== 'AbortError') console.error('Error fetching series', err);
          });
      }, 400);

      return () => {
        clearTimeout(delayDebounce);
        controller.abort();
      };
    } else {
      loadSeriesDetails(id);
    }
  }, [id, searchTerm]);

  const loadSeriesDetails = async (seriesId) => {
    setLoading(true);
    try {
      const res1 = await fetch(`/api/series/${seriesId}/matches`);
      const json1 = await res1.json();
      setMatches(json1.data?.matchList || []);

      const res2 = await fetch(`/api/series/${seriesId}/points`);
      const json2 = await res2.json();
      setPointsTable(json2.data || []);
    } catch (err) {
      console.error('Error loading series details', err);
    } finally {
      setLoading(false);
    }
  };

  if (!id) {
    // All series view with search bar
    return (
      <div className="series-container">
        <h2>All Series</h2>

        <input
          type="text"
          placeholder="Search series..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="series-search"
        />

        <div className="series-grid">
          {seriesList.map(s => (
            <div key={s.id} className="series-card" onClick={() => navigate(`/series/${s.id}`)}>
              <h3>{s.name}</h3>
              <p>{s.startDate} - {s.endDate}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Fixtures + Points table for a single series
  return (
    <div className="series-container">
      <button onClick={() => navigate(fromHome ? '/' : '/series')}>
        ‚Üê Back to {fromHome ? 'Home' : 'Series'}
      </button>

      <div className="tabs">
        <button onClick={() => setActiveTab('fixtures')} className={activeTab === 'fixtures' ? 'active' : ''}>Fixtures</button>
        <button onClick={() => setActiveTab('points')} className={activeTab === 'points' ? 'active' : ''}>Points Table</button>
      </div>

      {loading ? <p>Loading...</p> : (
        <>
          {activeTab === 'fixtures' && (
            matches.length > 0 ? (
              <div className="fixtures-list">
                {matches.map(match => (
                  <Link key={match.id} to={`/match/${match.id}`}>
                    <div className="fixture-card">
                      <strong>{match.name}</strong>
                      <p>{match.date}</p>
                      <p>{match.status}</p>
                    </div>
                  </Link>
                ))}
              </div>
            ) : <p>No matches found for this series.</p>
          )}

          {activeTab === 'points' && (
            pointsTable.length > 0 ? (
              <table className="points-table">
                <thead>
                  <tr>
                    <th>Team</th>
                    <th>M</th>
                    <th>W</th>
                    <th>L</th>
                    <th>T</th>
                    <th>NR</th>
                  </tr>
                </thead>
                <tbody>
                  {pointsTable.map(team => (
                    <tr key={team.shortname}>
                      <td>
                        <img src={team.img} alt={team.shortname} width={24} /> {team.teamname}
                      </td>
                      <td>{team.matches}</td>
                      <td>{team.wins}</td>
                      <td>{team.loss}</td>
                      <td>{team.ties}</td>
                      <td>{team.nr}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : <p>No points data for this series.</p>
          )}
        </>
      )}
    </div>
  );
};

export default Series;
