import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function PlayerDetail() {
  const { id } = useParams();
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    fetch(`/api/players/${id}`)
      .then(res => res.json())
      .then(setPlayer);
  }, [id]);

  if (!player) return <div>Loading...</div>;

  return (
    <div className="player-detail">
      <img src={player.image} alt={player.name} />
      <h2>{player.name}</h2>
      <p><b>Country:</b> {player.country}</p>
      <p><b>Date of Birth:</b> {player.dob}</p>
      <p><b>Batting Style:</b> {player.battingStyle}</p>
      <p><b>Bowling Style:</b> {player.bowlingStyle}</p>
      <p><b>Position:</b> {player.position}</p>

      <h3>Career</h3>
      {player.career.map((c, idx) => (
        <div key={idx}>
          <strong>{c.type}</strong> - Matches: {c.batting?.matches}, Runs: {c.batting?.runs_scored}
        </div>
      ))}
    </div>
  );
}

export default PlayerDetail;
